import{_ as e,c as l,o as n,ah as d}from"./chunks/framework.HzbgFGQH.js";const o=JSON.parse('{"title":"油温判断技巧及常见温度和单位换算表","description":"","frontmatter":{},"headers":[],"relativePath":"guide/advanced/油温判断技巧.md","filePath":"guide/advanced/油温判断技巧.md","lastUpdated":1747118079000}'),r={name:"guide/advanced/油温判断技巧.md"};function a(i,t,s,c,x,g){return n(),l("div",null,t[0]||(t[0]=[d('<h1 id="油温判断技巧及常见温度和单位换算表" tabindex="-1">油温判断技巧及常见温度和单位换算表 <a class="header-anchor" href="#油温判断技巧及常见温度和单位换算表" aria-label="Permalink to “油温判断技巧及常见温度和单位换算表”">​</a></h1><ul><li>油温在 120°C-140°C 之间：适合软炸[^1]、滑炒[^2], 把筷子放入油锅中，周围基本不起泡泡，无青烟、无响声、油温平静。</li><li>油温在 150°C-160°C 之间：最佳烹饪温度，把筷子放入油锅中，周围会冒出少许油泡，略有青烟，油从四周往中间翻动。</li><li>油温在 160°C-180°C 之间：适合上色炸酥，把筷子放入油锅中，大量青烟上升，油面反而较平静。</li><li>（注） 最好买把油温枪，谨慎使用温度计。</li></ul><blockquote><p>网络视频教程中所谓的 “几成油温” 指的是相对于十成油温，为 300°C。 即 n 成油温 T_n = 30n [degree Celsius] 温度换算公式 C = 5/9 <em>(F - 32) 或者 F = 9/5</em>C + 32</p></blockquote><h2 id="附油温对照表" tabindex="-1">附油温对照表 <a class="header-anchor" href="#附油温对照表" aria-label="Permalink to “附油温对照表”">​</a></h2><p>根据上述描述制表。误差为非专业数据。</p><table tabindex="0"><thead><tr><th style="text-align:center;">摄氏度 °C</th><th style="text-align:center;">常见名称</th><th style="text-align:center;">华氏度 °F</th></tr></thead><tbody><tr><td style="text-align:center;">-18±4</td><td style="text-align:center;">急冻（冷冻)</td><td style="text-align:center;">-0.4±4</td></tr><tr><td style="text-align:center;">4±2</td><td style="text-align:center;">保鲜</td><td style="text-align:center;">40±4</td></tr><tr><td style="text-align:center;">30±10</td><td style="text-align:center;">常温</td><td style="text-align:center;">86±18</td></tr><tr><td style="text-align:center;">60±10</td><td style="text-align:center;">二成</td><td style="text-align:center;">140±18</td></tr><tr><td style="text-align:center;">90±10</td><td style="text-align:center;">三成</td><td style="text-align:center;">194±18</td></tr><tr><td style="text-align:center;">120±10</td><td style="text-align:center;">四成</td><td style="text-align:center;">248±18</td></tr><tr><td style="text-align:center;">150±10</td><td style="text-align:center;">五成</td><td style="text-align:center;">302±18</td></tr><tr><td style="text-align:center;">180±10</td><td style="text-align:center;">六成</td><td style="text-align:center;">356±18</td></tr><tr><td style="text-align:center;">210±10</td><td style="text-align:center;">七成</td><td style="text-align:center;">410±18</td></tr><tr><td style="text-align:center;">240±10</td><td style="text-align:center;">八成</td><td style="text-align:center;">464±18</td></tr><tr><td style="text-align:center;">270±10</td><td style="text-align:center;">九成</td><td style="text-align:center;">518±18</td></tr><tr><td style="text-align:center;">300±10</td><td style="text-align:center;">十成</td><td style="text-align:center;">572±18</td></tr></tbody></table><ul><li>^1: 软炸是将小块、片或条形的材料挂糊，放入油锅，炸成七八成熟的油炸方法</li><li>^2: 滑炒，选用质嫩的动物性原料经过改刀切成丝、片、丁、条等形状，用蛋清、淀粉上浆，用温油滑散，倒入漏勺沥去余油</li></ul>',7)]))}const _=e(r,[["render",a]]);export{o as __pageData,_ as default};
