import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E6%B2%B9%E7%84%96%E5%A4%A7%E8%99%BE.NgyBijSK.jpg",p=JSON.parse('{"title":"油焖大虾的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/aquatic/油焖大虾/油焖大虾.md","filePath":"guide/aquatic/油焖大虾/油焖大虾.md","lastUpdated":1747034379000}'),o={name:"guide/aquatic/油焖大虾/油焖大虾.md"};function s(u,l,n,d,c,h){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="油焖大虾的做法" tabindex="-1">油焖大虾的做法 <a class="header-anchor" href="#油焖大虾的做法" aria-label="Permalink to &quot;油焖大虾的做法&quot;">​</a></h1><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>黑虎虾 or 明虾、</li><li>葱、姜</li><li>料酒、盐、冰糖、植物油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>1 人食用的版本。</p><ul><li>虾 10 只</li><li>花椒 5g</li><li>葱 50g</li><li>姜 20g</li><li>黄酒 30g</li><li>盐 3g</li><li>冰糖 10g</li><li>植物油</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>剪虾枪到根上，虾须虾爪都剪掉，沙包挑掉，开背虾线挑出来，洗净备用</li><li>炸料油 <ul><li>油温三成热放花椒，油热离火，放葱姜（不要让油变色最好）葱稍微变黄沥油 （葱油弄多了可以留着作为拌面使用）。</li></ul></li><li>下油，虾摆放整齐，两面变色后轻轻摁虾头 <ul><li>放姜米（姜切成细颗粒）</li><li>黄酒 30g</li><li>水两小碗</li><li>盐 3g</li><li>冰糖 10 克</li></ul></li><li>大火烧开转小火盖盖子闷（中途不能再加汤水，不要开盖）</li><li>皮亮虾弯就可以起锅，虾摆盘</li><li>收汁（过滤后倒回锅里收浓，放葱油 ） 汤汁剩余 1/4 时。</li><li>浇汁</li><li>完成</li><li><img src="'+r+'" alt="成品"></li><li>开吃✅</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>做法参考：B 站老饭骨视频<a href="https://www.bilibili.com/video/BV17f4y1W7z9" target="_blank" rel="noreferrer">从小吃到大的油焖大虾居然是假的？泰斗级大师还原真·味道</a></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const g=i(o,[["render",s]]);export{p as __pageData,g as default};
