import{_ as i,c as a,o as e,ah as t}from"./chunks/framework.HzbgFGQH.js";const r="/assets/%E7%83%A4%E9%B1%BC.IFANDztt.jpg",p=JSON.parse('{"title":"烤鱼的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/aquatic/混合烤鱼/烤鱼.md","filePath":"guide/aquatic/混合烤鱼/烤鱼.md","lastUpdated":1747108270000}'),s={name:"guide/aquatic/混合烤鱼/烤鱼.md"};function n(d,l,o,c,h,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="烤鱼的做法" tabindex="-1">烤鱼的做法 <a class="header-anchor" href="#烤鱼的做法" aria-label="Permalink to “烤鱼的做法”">​</a></h1><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>草鱼（农贸市场或者超市让店家杀掉，去除不要的器官）</li><li>大葱</li><li>料酒</li><li>白胡椒粉</li><li>食用盐</li><li>大蒜</li><li>桂皮</li><li>八角</li><li>香叶</li><li>青花椒</li><li>干辣椒段</li><li>灯笼椒</li><li>火锅底料（随意）</li><li>千张</li><li>绿豆芽</li><li>洋葱</li><li>豆瓣酱</li><li>芹菜段</li><li>熟花生米</li><li>白芝麻</li><li>香菜（放更好吃，根据个人口味可放可不放）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>草鱼 大约三斤</li><li>大葱 半根</li><li>食用油 20ml</li><li>料酒 10-15ml</li><li>食用盐 5-10g</li><li>白胡椒粉 5g-10g</li><li>桂皮 一小片</li><li>八角 两个</li><li>大蒜粒 八个</li><li>香叶 两张</li><li>青花椒 一小把</li><li>干辣椒段 10 个</li><li>灯笼椒 4 个</li><li>芹菜段 两根</li><li>洋葱 半个</li><li>千张 一张</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>草鱼（一般 3 斤 ）从背部切开，两面沿着鱼的背部往下划几刀，不要划到鱼肚皮，不然不易定型 <ul><li>可以用热水浇在鱼身上洗去粘液或者用刷子在流动水龙头下面不停的刷洗，直到摸着没有黏糊糊的手感。</li></ul></li><li>把鱼放到容器中，加入料酒，10g 白胡椒粉，5g 食盐抹匀腌制二十分钟入味。</li><li>把半根大葱切成一块一块，大蒜粒中间切开，和八角香叶桂皮放在一个容器中</li><li>干辣椒段中间一分为二切开并和灯笼椒装在一个容器中</li><li>芹菜切小段</li><li>豆芽焯水</li><li>千张焯水切成丝</li><li>洋葱切成丝。</li><li>烤制鱼 <ul><li>家里有烤箱的可以在烤盘刷上底油，鱼皮朝下，直到烤制两面金黄，然后撒上孜然粉</li><li>如果没有烤箱，可以热锅热油，锅的两边撒上 2g 食盐，下入草鱼开始煎，刚下入的时候不要着急翻动，等一面定型后再翻面，煎制两面金黄，撒上孜然粉，出锅装在盘子里准备。</li></ul></li><li>锅中撒上 20ml 食用油，等到油热后，把大葱大蒜八角香叶倒入炒香</li><li>加上一包火锅底料的一半和 15-20g 豆瓣酱，炒出红油</li><li>加入 5g 白糖，10g 食盐，5ml 生抽调味，倒入和食材齐平的清水煮开</li><li>依次下入芹菜段，豆芽，千张丝，不用煮熟，稍微烫一下后铺上洋葱丝，放上烤鱼</li><li>加入干辣椒，灯笼椒，青花椒</li><li>另一个锅烧油，油热后浇在刚加入的辣椒上面激发出香味</li><li>最后撒上熟花生米，葱花，白芝麻，香菜</li><li>煮 5-6 分钟，美味即成。</li></ul><p><img src="'+r+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>技术总结：上述 <ul><li>这道菜的食盐，胡椒粉，孜然粉，食用油，生抽，白糖等的使用量，根据个人口味和食材比例做决定，并不需要严格按照上述分量来做。</li></ul></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const m=i(s,[["render",n]]);export{p as __pageData,m as default};
