import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E6%94%B9%E5%88%80.DnjskLK2.jpg",s="/assets/%E6%91%86%E7%9B%98.BFEsDCbl.jpg",o="/assets/%E6%B8%85%E8%92%B8%E9%B2%88%E9%B1%BC.DbkEuYec.jpg",q=JSON.parse('{"title":"清蒸鲈鱼的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/aquatic/清蒸鲈鱼/清蒸鲈鱼.md","filePath":"guide/aquatic/清蒸鲈鱼/清蒸鲈鱼.md","lastUpdated":1747107234000}'),u={name:"guide/aquatic/清蒸鲈鱼/清蒸鲈鱼.md"};function n(c,l,d,_,h,p){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="清蒸鲈鱼的做法" tabindex="-1">清蒸鲈鱼的做法 <a class="header-anchor" href="#清蒸鲈鱼的做法" aria-label="Permalink to &quot;清蒸鲈鱼的做法&quot;">​</a></h1><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>鲈鱼（害怕杀鱼的同学可以让店家帮忙杀）</li><li>香葱</li><li>姜</li><li>食用油</li><li>蒸鱼豉油</li><li>料酒</li><li>食用盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>鲈鱼 一条</li><li>香葱 三根</li><li>姜 一块</li><li>食用油 10-15ml</li><li>蒸鱼豉油 10-15ml</li><li>料酒 10-15ml</li><li>食用盐 5-10g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>姜切片切丝、香葱的葱白切段，葱绿切丝，切丝后放入冷水浸泡备用。</li><li>鲈鱼处理好后洗净，用厨房纸擦干，两面分别划几刀，用盐洗掉鱼身的粘液，并用 10g 盐抹遍鱼身的内外，腌制 10 分钟以上。</li><li>补充一个鲈鱼改刀和摆盘的方法，改刀后可以让鲈鱼立起来蒸，均匀受热，同时吃起来更加方便，无需翻面。</li><li><img src="'+r+'" alt="改刀"></li><li><img src="'+s+'" alt="摆盘"></li><li>鱼肚内塞上姜和葱白，鱼身也撒上姜和葱白，量为备用的一半。蒸鱼的碟子用筷子将鱼跟碟子隔开蒸</li><li>水烧热感觉到水温后放进入鱼</li><li>大火清蒸 10 分钟。</li><li>蒸好的鱼，用干净的盘子装起来并去除身上姜蒜</li><li>鱼身浇上 15ml 蒸鱼豉油</li><li>鱼身重新撒上姜和葱丝，锅内加上 10ml 食用油并烧热，将食用油淋至鱼身即可出菜</li></ul><p><img src="'+o+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>技术总结： <ul><li>这道菜属于有手就行，关键点在于火候，鱼的大小跟火候都会相关，太久会导致鱼肉太老极度影响口感，太短会导致部分鱼肉没熟。所以大火蒸鱼一般是 10 分钟内较佳。</li><li>切记蒸鱼需要用筷子隔开装鱼的盘子，这样做的好处有两点： <ul><li>1、鱼在蒸的过程中会将水滴到盘子，如果鱼直接接触会导致鱼食用时会腥。</li><li>2、能够将鱼均匀受热。</li></ul></li><li>这道菜难度系数简单，而且味道非常棒哦</li></ul></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const f=i(u,[["render",n]]);export{q as __pageData,f as default};
