import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E8%8A%A5%E6%9C%AB%E9%BB%84%E6%B2%B9%E7%BD%97%E6%B0%8F%E8%99%BE.D1QY0Rpu.jpg",p=JSON.parse('{"title":"芥末黄油罗氏虾的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/aquatic/芥末黄油罗氏虾/芥末黄油罗氏虾.md","filePath":"guide/aquatic/芥末黄油罗氏虾/芥末黄油罗氏虾.md","lastUpdated":1747034379000}'),o={name:"guide/aquatic/芥末黄油罗氏虾/芥末黄油罗氏虾.md"};function s(u,l,n,d,_,c){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="芥末黄油罗氏虾的做法" tabindex="-1">芥末黄油罗氏虾的做法 <a class="header-anchor" href="#芥末黄油罗氏虾的做法" aria-label="Permalink to &quot;芥末黄油罗氏虾的做法&quot;">​</a></h1><p><img src="'+r+'" alt="芥末黄油罗氏虾"> 这是一道做法简单，味道美味，具有新意的海鲜菜。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>罗氏虾</li><li>黄油</li><li>芥末</li><li>白糖</li><li>生抽</li><li>蚝油</li><li>盐</li><li>料酒、朗姆酒或啤酒</li><li>香菜</li><li>蒜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>按照 1 盘的份量：</p><ul><li>罗氏虾 1 斤多 广东市场价大概 40~45 一斤</li><li>黄油 约 20g</li><li>芥末 15g</li><li>白糖 3g</li><li>生抽 30g</li><li>蚝油 30g</li><li>盐 3g</li><li>料酒、朗姆酒或啤酒 15g 到 30g</li><li>香菜 5 条 切段</li><li>蒜 5 颗 剁成蒜蓉</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将罗氏虾剪掉头尾尖刺、触须和脚，剪刀把虾身开背，去除虾线。</li><li>提前搅拌好芥末酱汁：酱油、蚝油、芥末、盐、糖，搅拌均匀！</li><li>洗好香菜，切段备用。</li><li>罗氏虾沥掉水，锅中加入油，直接放入罗氏虾，中火，外表煎至金黄，捞出。</li><li>下入蒜蓉，大火，利用煎虾剩下的油继续煎炒蒜蓉，等到锅中白雾冒出，蒜蓉已经煎出香味，下虾和黄油，让虾充分吸收黄油香味</li><li>下入调好的酱汁，继续大火煮沸，翻炒虾，至酱汁收汁，加入酒（料酒、啤酒可以放 30g，朗姆酒味道浓郁放 15g 即可。）</li><li>在等酱汁稍微收汁，加入香菜翻炒两下，即可出锅。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>酱汁提前调配好，黄油提前备好，防止中间备料耗时。</li><li>蒜蓉切记要等虾煎好后捞出再下，防止煎炒过久变黑，发苦。</li><li>如果不太能接受芥末就放少点，能接受就放多点，如果放多了，煮久一会儿，芥末味也会散去。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const g=i(o,[["render",s]]);export{p as __pageData,g as default};
