import{_ as a,c as e,o as t,ah as s}from"./chunks/framework.HzbgFGQH.js";const i="/assets/%E8%91%B1%E7%83%A7%E6%B5%B7%E5%8F%82.D1ShsVO0.jpeg",r="/assets/%E6%B5%B7%E5%8F%82.BNFbVhzx.jpeg",o="/assets/%E9%85%B1%E6%B1%81.DhRtZA0A.jpeg",n="/assets/%E8%91%B1%E7%99%BD.BxNTk2Ox.jpeg",f=JSON.parse('{"title":"葱烧海参的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/aquatic/葱烧海参/葱烧海参.md","filePath":"guide/aquatic/葱烧海参/葱烧海参.md","lastUpdated":1747108385000}'),c={name:"guide/aquatic/葱烧海参/葱烧海参.md"};function m(_,l,d,h,p,g){return t(),e("div",null,l[0]||(l[0]=[s('<h1 id="葱烧海参的做法" tabindex="-1">葱烧海参的做法 <a class="header-anchor" href="#葱烧海参的做法" aria-label="Permalink to “葱烧海参的做法”">​</a></h1><p><img src="'+i+'" alt="示例菜成品"></p><p>这道菜的做法并不难，就是海参泡发是需要时间的。疫情隔离在家，干海参是过年前存的年货，正好拿出来尝试一下。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>泡发好的海参<img src="'+r+'" alt="海参"></li><li>大葱葱白</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>泡发好的海参（北极参） 4 个</li><li>大葱葱白 1 根大葱的葱白即可</li><li>食用油 20-25ml</li><li>蚝油 20g</li><li>生抽 5g</li><li>白糖 2g</li><li>淀粉 2g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>葱白切成 1cm 的段，备用。</li><li>海参切成 1cm 的段，备用。</li><li>准备一个空碗，倒入 20g 蚝油， 10g 生抽， 2g 白糖，搅拌均匀。<img src="'+o+'" alt="料汁"></li><li>另一个空碗倒入淀粉，水，制备水淀粉，勾芡用。</li><li>热锅，锅内放入 20ml - 25ml 食用油。等待 10 秒让油温升高。</li><li>放入葱白，调<em>小火</em>，注意不要让葱白变焦。大概煎 3-5 分钟即可。<img src="'+n+'" alt="葱白"></li><li>用筷子夹出葱白，放入盘中备用。</li><li>倒入调好的料汁，炒香，<strong>等待 1 - 2 分钟</strong> 。</li><li>放入切好的海参，翻炒 1 分钟</li><li>加入 100 ml 的水， 中小火， <strong>等待 5 分钟</strong></li><li>等待锅中汤汁快干的时候，加入水淀粉，加入前面取出的葱白</li><li>在外观<em>呈粘稠状态</em>后关火，盛盘 <img src="'+i+'" alt="成品"></li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>操作时，需要注意观察锅中的水量，如快见底的时候就直接接入水淀粉即可。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const B=a(c,[["render",m]]);export{f as __pageData,B as default};
