import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E9%A6%99%E7%85%8E%E7%BF%98%E5%98%B4%E9%B1%BC.CEk71NQm.jpeg",p=JSON.parse('{"title":"香煎翘嘴鱼的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/aquatic/香煎翘嘴鱼/香煎翘嘴鱼.md","filePath":"guide/aquatic/香煎翘嘴鱼/香煎翘嘴鱼.md","lastUpdated":1747034379000}'),o={name:"guide/aquatic/香煎翘嘴鱼/香煎翘嘴鱼.md"};function s(n,l,u,d,c,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="香煎翘嘴鱼的做法" tabindex="-1">香煎翘嘴鱼的做法 <a class="header-anchor" href="#香煎翘嘴鱼的做法" aria-label="Permalink to &quot;香煎翘嘴鱼的做法&quot;">​</a></h1><p><img src="'+r+'" alt="香煎翘嘴鱼"></p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>翘嘴鱼（肉食性鱼类，肉细腻，口感好）</li><li>姜</li><li>葱</li><li>蒜</li><li>青椒</li><li>香菜</li><li>老抽</li><li>白糖</li><li>豆瓣酱</li><li>料酒</li><li>生抽</li><li>盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>注：这个量大概是 2-3 个人的量</p><p>每份：</p><ul><li>翘嘴鱼：2 斤最佳</li><li>姜沫：20g</li><li>葱：半根（50 克）</li><li>蒜：4 个</li><li>香菜：个人口味</li><li>老抽：2ml（不太喜欢重口的可以不放）</li><li>白糖：10g</li><li>干辣椒：4-6 个（根据个人口味选择）</li><li>料酒：100ml</li><li>生抽：4ml</li><li>盐：约 50g 用于腌制鱼</li><li>食用油：100ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>鱼开背杀好（让卖鱼的杀好，千万不要剖腹杀鱼，切记是开背），清洗干净</li><li>鱼表面用盐涂抹均匀，倒入料酒约 80ml,姜末 20g，放入冰箱保鲜层进行腌制 1-2 天</li><li>取出腌制好的鱼，用绳挂起晾晒至半干（约 1-2 天，具体时间需结合气温与阳光）</li><li>食用前请将鱼用清水清洗，沥干水分（防止水遇油飞溅）</li><li>开大火将锅烧热，迅速改小火，锅中放油，尽量保持整个锅表面有油，将鱼沿锅边划入锅内（先煎鱼背面）</li><li>鱼入锅后（和翻面后），不要着急移动鱼的位置（此时容易破皮），煎约 30 秒后，尝试晃动锅</li><li>背面煎约 1 分钟后，翻面煎约 1-2 分钟，煎至两面金黄</li><li>等两面都煎好时，把鱼推向锅边一点，留点空间放入豆瓣酱炒香味，放入姜蒜，</li><li>炒出佐料香味后，加入料酒，生抽，老抽，倒入热水，水量和鱼平齐或者少点</li><li>此时改中大火，煮 5-10 分钟，后放入青椒断，白糖，鸡精，十三香，陈醋</li><li>改小火 2-5 分钟，放入葱，香菜，即可出锅</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>切记鱼是开背杀，腌制时，鱼表面涂抹均匀食盐即可，腌制时间，晾晒时间 1-2 天即可</li><li>煎鱼全程小火，刚入锅时不要移动鱼，会破皮</li><li>青椒断放入后注意观察熟透程度，里面的青椒很好吃，另外香菜最后放</li><li>注意火候的切换，豆瓣酱，白糖的调鲜效果，与陈醋的增香效果</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=i(o,[["render",s]]);export{p as __pageData,m as default};
