import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E9%B2%A4%E9%B1%BC%E7%82%96%E7%99%BD%E8%8F%9C.B6wq474n.jpeg",p=JSON.parse('{"title":"鲤鱼炖白菜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/aquatic/鲤鱼炖白菜/鲤鱼炖白菜.md","filePath":"guide/aquatic/鲤鱼炖白菜/鲤鱼炖白菜.md","lastUpdated":1747107234000}'),o={name:"guide/aquatic/鲤鱼炖白菜/鲤鱼炖白菜.md"};function s(n,l,u,d,c,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="鲤鱼炖白菜的做法" tabindex="-1">鲤鱼炖白菜的做法 <a class="header-anchor" href="#鲤鱼炖白菜的做法" aria-label="Permalink to &quot;鲤鱼炖白菜的做法&quot;">​</a></h1><p><img src="'+r+'" alt="鲤鱼炖白菜"></p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>食用油</li><li>姜</li><li>蒜</li><li>鲤鱼</li><li>白菜心/娃娃菜</li><li>盐</li><li>老抽</li><li>生抽</li><li>桂皮</li><li>八角</li><li>郫县豆瓣酱</li><li>干辣椒（不吃辣可以不放）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>注：调料根据鱼的大小进行换算。</p><p>每份：</p><ul><li>食用油：10ml</li><li>姜：3 片</li><li>蒜：3 瓣（切成块）</li><li>鲤鱼：1.2 斤（清理过的）</li><li>娃娃菜：13 片（可以多放一些，一顿就缩小了）</li><li>盐：5-8 克</li><li>老抽：3ml</li><li>生抽：6ml</li><li>桂皮：1 块</li><li>八角：3 个</li><li>郫县豆瓣酱：20 克</li><li>干辣椒：4-6 个（根据个人口味选择）</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>鲤鱼清洗干净，改刀（在鱼身上多划几个伤口，方便入味）</li><li>娃娃菜清洗干净放入盘中备用</li><li>锅中加油，等油热放入“少盐” “姜” “蒜” “郫县豆瓣酱” “桂皮” “八角” 炒出香味</li><li>把鱼放锅里煎（3 分钟）每（30 秒）需要翻面</li><li>加入“水”（水量尽量和鱼平齐，可以少一点点） 放入 “生抽” “老抽” “娃娃菜”</li><li>大火炖 15-20 分钟，汤汁快干时添加 “盐” 即可出锅</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>也可以放一些粉条，粉条先凉水/热水，需要泡软，可以和娃娃菜一起放入</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=i(o,[["render",s]]);export{p as __pageData,m as default};
