import{_ as i,c as a,o as e,ah as t}from"./chunks/framework.HzbgFGQH.js";const r="/assets/%E9%B3%8A%E9%B1%BC%E7%82%96%E8%B1%86%E8%85%90.DlR6lYu8.jpg",u=JSON.parse('{"title":"鳊鱼炖豆腐的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/aquatic/鳊鱼炖豆腐/鳊鱼炖豆腐.md","filePath":"guide/aquatic/鳊鱼炖豆腐/鳊鱼炖豆腐.md","lastUpdated":1747108385000}'),s={name:"guide/aquatic/鳊鱼炖豆腐/鳊鱼炖豆腐.md"};function n(d,l,o,c,h,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="鳊鱼炖豆腐的做法" tabindex="-1">鳊鱼炖豆腐的做法 <a class="header-anchor" href="#鳊鱼炖豆腐的做法" aria-label="Permalink to “鳊鱼炖豆腐的做法”">​</a></h1><p><img src="'+r+'" alt="鳊鱼炖豆腐"></p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>鳊鱼（鱼可以让摊主帮忙处理好）</li><li>老豆腐</li><li>姜</li><li>葱</li><li>蒜</li><li>老抽</li><li>桂皮（可选）</li><li>冰糖</li><li>干辣椒（不吃辣可以不放）</li><li>料酒</li><li>生抽</li><li>盐</li><li>八角（可选）</li><li>香叶（可选）</li><li>热水</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>注：这个量大概是 2-3 个人的量</p><p>每份：</p><ul><li>鳊鱼：550 克</li><li>老豆腐：400 克</li><li>姜：5 片</li><li>葱：半根（50 克）</li><li>蒜：4 个</li><li>老抽：2ml（不太喜欢重口的可以不放）</li><li>桂皮：1 块</li><li>冰糖：5 块</li><li>干辣椒：4-6 个（根据个人口味选择）</li><li>料酒：5ml</li><li>生抽：4ml</li><li>盐：5-8 克（根据个人口味选择）</li><li>八角：1 个</li><li>香叶：1-3 片</li><li>食用油：10ml</li><li>热水：400 克</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>鳊鱼改刀,放上姜片和料酒腌制 5-10 分钟</li><li>老豆腐切块后放入水中备用</li><li>锅中加油，可以放点盐在锅里，防止煎鱼的时候粘锅，把腌制的鱼用厨房纸擦干水分，把鱼放到锅中，两面都煎一下</li><li>等两面都煎好时，把鱼推向锅边一点，留点空间放入葱姜蒜，干辣椒，香叶，八角炒出味道</li><li>炒出佐料香味后，加入料酒，生抽，老抽，冰糖，桂皮，倒入热水，水量和鱼平齐或者少点</li><li>大火烧开后，放入老豆腐，豆腐贴在锅边，加入食盐，转小火</li><li>小火烧 10-15 分钟，然后大火收点汁，即可出锅</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>鱼的两面，各煎 2-4 分钟即可</li><li>煎鱼的时候全程中小火</li><li>最后出锅时，加入一些切碎的大蒜，味道会更好</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=i(s,[["render",n]]);export{u as __pageData,m as default};
