import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E9%BB%84%E6%B2%B9%E7%85%8E%E8%99%BE.CuXcvxj3.jpg",p=JSON.parse('{"title":"黄油煎虾的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/aquatic/黄油煎虾/黄油煎虾.md","filePath":"guide/aquatic/黄油煎虾/黄油煎虾.md","lastUpdated":1747107234000}'),o={name:"guide/aquatic/黄油煎虾/黄油煎虾.md"};function s(n,l,d,u,c,h){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="黄油煎虾的做法" tabindex="-1">黄油煎虾的做法 <a class="header-anchor" href="#黄油煎虾的做法" aria-label="Permalink to &quot;黄油煎虾的做法&quot;">​</a></h1><p><img src="'+r+'" alt="示例菜成品"></p><p>黄油煎虾是一道制作相对简单、风味极佳的菜式，主要耗时在于处理活虾，总耗时在一个小时内，适合初学者进行烹饪。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>鲜虾（强推肉质紧实的九节虾，普通明虾也可以）</li><li>黄油（推荐安佳，一次用一小盒 7g）</li><li>黑胡椒粒（瓶磨的那种）</li><li>生抽</li><li>盐</li><li>白糖</li><li>米酒</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>鲜虾 300g</li><li>黄油 7g</li><li>黑胡椒粒 大概 15ml</li><li>食用油 45ml</li><li>生抽 10ml</li><li>米酒 5ml</li><li>白糖 10ml</li><li>盐 2.5ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>鲜虾摘除头部，顺带扯出虾线（这步处理不好可在下一步开背时取出虾线），使用剪刀剪开或菜刀片开虾背，沥干水分备用</li><li>调制酱汁：小碗放入上述量的全部生抽、米酒、白糖、盐搅匀备用</li><li>中大火热锅，热锅内放入食用油，等待 10 秒让油温升高</li><li>虾全部放入锅中，开始瓶磨黑胡椒，均匀地撒在虾上翻炒</li><li>虾变色后加入黄油，黄油完全融化后倒入调制酱汁，继续翻炒</li><li>大火翻炒 15 秒收汁即可装盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>虾开背才更好入味，不过处理时切记切记小心用刀，新手容易伤到手</li><li><a href="https://www.bilibili.com/video/BV1g541177cd" target="_blank" rel="noreferrer">冯小厨的菜谱</a></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=i(o,[["render",s]]);export{p as __pageData,m as default};
