import{_ as e,c as l,o as i,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"吐司果酱的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/breakfast/吐司果酱.md","filePath":"guide/breakfast/吐司果酱.md","lastUpdated":1747107234000}'),r={name:"guide/breakfast/吐司果酱.md"};function o(s,a,d,n,h,u){return i(),l("div",null,a[0]||(a[0]=[t('<h1 id="吐司果酱的做法" tabindex="-1">吐司果酱的做法 <a class="header-anchor" href="#吐司果酱的做法" aria-label="Permalink to &quot;吐司果酱的做法&quot;">​</a></h1><p>饱腹感的懒人快速营养早餐，2 分钟 搞定</p><p>预估烹饪难度：★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>新鲜吐司</li><li>果酱</li><li>面包机</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>吐司两片</li><li>果酱足够涂满一面吐司的量</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将吐司放入面包机</li><li>设置好档位,时间到了会自动弹出</li><li>两分钟后吐司加热完成弹出</li><li>先取出一片吐司,涂满果酱再盖上另一片吐司即可</li><li>用餐巾纸包一下可以边走边吃也可以吃完再出门</li></ul><p>两分钟快速搞定,操作很简单,味道十分美味,十分适合程序员。耗时短,不会产生额外垃圾,也不需要清洗工具什么的。</p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>面包机一般不会超过一百块,吐司去楼下超市或美团买菜送上门,一般一包十块钱八片,保质期比较短,很干净卫生。</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=e(r,[["render",o]]);export{c as __pageData,p as default};
