import{_ as a,c as i,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const _=JSON.parse('{"title":"太阳蛋的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/breakfast/太阳蛋.md","filePath":"guide/breakfast/太阳蛋.md","lastUpdated":1747107234000}'),r={name:"guide/breakfast/太阳蛋.md"};function o(h,l,s,u,d,n){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="太阳蛋的做法" tabindex="-1">太阳蛋的做法 <a class="header-anchor" href="#太阳蛋的做法" aria-label="Permalink to &quot;太阳蛋的做法&quot;">​</a></h1><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>鸡蛋</li><li>盐</li><li>油</li><li>分可控火候微波炉或不可控火候微波炉（定义和分辨方式请见附加内容）</li><li>筷子或牙签</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>鸡蛋的用量为 1 个。</li><li>盐的用量为 1 g 每个鸡蛋。</li><li>油的用量为 5 mL 每个鸡蛋。</li></ul><p>使用上述条件，计算出计划使用的原材料比例。</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><h3 id="可控火候微波炉" tabindex="-1">可控火候微波炉 <a class="header-anchor" href="#可控火候微波炉" aria-label="Permalink to &quot;可控火候微波炉&quot;">​</a></h3><ul><li>准备一个小碗，倒入在上一步计算好的油，撒盐，搅拌均匀。倾斜碗使油沾在碗表面。</li><li>取出一个鸡蛋，打入小碗。</li><li>蛋黄表面戳孔。牙签戳 5 个或筷子戳 1 个。</li><li>放入微波炉，中火 3 分钟。</li></ul><h3 id="不可控火候微波炉" tabindex="-1">不可控火候微波炉 <a class="header-anchor" href="#不可控火候微波炉" aria-label="Permalink to &quot;不可控火候微波炉&quot;">​</a></h3><ul><li>准备一个小碗，倒入在上一步计算好的油，撒盐，搅拌均匀。倾斜碗使油沾在碗表面。</li><li>取出一个鸡蛋，打入小碗。</li><li>蛋黄表面戳孔。牙签戳 5 个或筷子戳 1 个。</li><li>放入微波炉，1 分钟。</li><li>while（太阳蛋 否 大面积成固体状） 用微波炉打(30s);</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>while（太阳蛋的熟度 不符合 个人口味） 用微波炉打（1 分钟）;</p><ul><li>不可控火候微波炉： <ul><li>定义： 即无法控制火候仅能控制时长的微波炉。</li><li>辨别方法： 若在微波炉操作面板上无法找到小火，中火， 大火等字样即为不可控火候微波炉</li></ul></li><li>可控火候微波炉： <ul><li>定义：即能控制火候又能控制时长的微波炉，</li><li>辨别方法： 若在微波炉操作面板上能找到小火，中火，大火等字样即为可控火候微波炉</li></ul></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',16)]))}const p=a(r,[["render",o]]);export{_ as __pageData,p as default};
