import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"手抓饼的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/breakfast/手抓饼.md","filePath":"guide/breakfast/手抓饼.md","lastUpdated":1747107234000}'),r={name:"guide/breakfast/手抓饼.md"};function o(s,l,h,d,n,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="手抓饼的做法" tabindex="-1">手抓饼的做法 <a class="header-anchor" href="#手抓饼的做法" aria-label="Permalink to &quot;手抓饼的做法&quot;">​</a></h1><p>预估烹饪难度：★★</p><hr><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>普通面粉</li><li>开水</li><li>冷水</li><li>食用油</li><li>盐</li><li>鸡蛋</li><li>生菜</li><li>火腿</li><li>芝士片</li></ul><hr><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 1~2 人食用。</p><p>总量（按每份）：</p><ul><li>面粉 200 克</li><li>开水 100 毫升</li><li>冷水 50 毫升</li><li>食用油 15 毫升</li><li>盐 3 克</li><li>鸡蛋 1 个</li><li>生菜 30 克</li><li>火腿 30 克</li><li>芝士片 1 片</li></ul><hr><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ol><li>面粉放入碗中，加入开水搅拌成絮状，再加入冷水揉成光滑面团，覆盖湿布静置 20 分钟。</li><li>面团分成每份约 100 克，搓圆，擀成薄片。</li><li>表面均匀涂抹食用油，撒上盐，卷成蜗牛状，松弛 10 分钟。</li><li>面团再次擀成薄饼，厚度均匀。</li><li>热锅中倒入油，小火煎至两面金黄起泡。</li><li>煎好的饼依次铺入煎蛋、生菜、火腿、芝士片等配料，卷起即可。</li></ol><hr><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>使用开水与冷水混合和面，有助于提升饼皮柔韧度。</li><li>面团醒发时间不宜少于 20 分钟，否则不易擀薄。</li><li>可根据个人口味增减配料，建议配料总重控制在 100 克以内。</li><li>剩余生饼皮可冷藏保存 24 小时，使用时回温擀平即可。</li></ul><hr><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',18)]))}const p=i(r,[["render",o]]);export{c as __pageData,p as default};
