import{_ as l,c as e,o as i,ah as r}from"./chunks/framework.HzbgFGQH.js";const u=JSON.parse('{"title":"煎饺的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/breakfast/煎饺.md","filePath":"guide/breakfast/煎饺.md","lastUpdated":1747108270000}'),t={name:"guide/breakfast/煎饺.md"};function s(d,a,n,h,o,c){return i(),e("div",null,a[0]||(a[0]=[r('<h1 id="煎饺的做法" tabindex="-1">煎饺的做法 <a class="header-anchor" href="#煎饺的做法" aria-label="Permalink to “煎饺的做法”">​</a></h1><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>饺子（速冻水饺）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>饺子一包 （根据个人食量选择， 约 10 - 15 个）</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>取出平底锅（不沾平底锅最佳）</li><li>加入 10ml - 15 ml 食用油</li><li>开火，放入饺子（尽量平均铺开，不宜堆叠）</li><li>立刻加入清水，水线没过饺子平均高度的 1/2</li><li>盖上锅盖（此时炉灶应该处于大火）</li><li>等待 8 - 10 分钟</li><li>当锅中水分仅剩 2mm 时， 转中火开始煎制</li><li>当水分全部蒸发后，摇晃平底锅使饺子受热均匀</li><li>放入黑芝麻和葱花再焖 10s</li><li>1 - 2 分钟夹出一个饺子观察底部，若出现金黄色脆皮立即取出</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>操作时，需时刻观察锅内情况，切记不可分神玩手机。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const m=l(t,[["render",s]]);export{u as __pageData,m as default};
