import{_ as l,c as e,o as i,ah as r}from"./chunks/framework.HzbgFGQH.js";const _=JSON.parse('{"title":"蛋煎糍粑的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/breakfast/蛋煎糍粑.md","filePath":"guide/breakfast/蛋煎糍粑.md","lastUpdated":1747108385000}'),t={name:"guide/breakfast/蛋煎糍粑.md"};function s(n,a,d,p,h,o){return i(),e("div",null,a[0]||(a[0]=[r('<h1 id="蛋煎糍粑的做法" tabindex="-1">蛋煎糍粑的做法 <a class="header-anchor" href="#蛋煎糍粑的做法" aria-label="Permalink to “蛋煎糍粑的做法”">​</a></h1><p>蛋煎糍粑做法很简单，不需要太多的厨艺基础~</p><p>蛋煎糍粑热量高，美味+顶饿+便宜，只需十分钟就可以完成~</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>鸡蛋</li><li>糍粑</li><li>白糖或红糖</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>糍粑 两块</li><li>红糖 10g （建议 8g - 15g 之间）</li><li>鸡蛋 1 个</li><li>食用油 10-15ml</li><li>食用盐 2g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>把糍粑切成长方形小块，便于后面煎</li><li>碗里打入一个鸡蛋并把鸡蛋搅碎，加入 2g 食用盐</li><li>将切好的小糍粑依此放入搅碎的鸡蛋里面，涂抹完糍粑双面为止</li><li>锅里倒入植物油 10ml ，把涂抹好的糍粑小块放进去小火慢慢煎软。</li><li>将剩下的鸡蛋液慢慢倒在糍粑表面</li><li>用筷子或者勺子为糍粑翻面，来回煎至金黄色后开吃</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li><p>放糍粑进去时候注意把糍粑间隔开来，防止粘在一起</p></li><li><p>鸡蛋的量根据糍粑的量而定</p></li><li><p>糖的量根据个人喜好</p></li><li><p><a href="https://www.dachu.co/recipe/378826" target="_blank" rel="noreferrer">蛋煎糍粑教程</a></p></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const u=l(t,[["render",s]]);export{_ as __pageData,u as default};
