import{_ as a,c as i,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"鸡蛋三明治的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/breakfast/鸡蛋三明治.md","filePath":"guide/breakfast/鸡蛋三明治.md","lastUpdated":1747034379000}'),r={name:"guide/breakfast/鸡蛋三明治.md"};function o(s,l,d,n,u,h){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="鸡蛋三明治的做法" tabindex="-1">鸡蛋三明治的做法 <a class="header-anchor" href="#鸡蛋三明治的做法" aria-label="Permalink to &quot;鸡蛋三明治的做法&quot;">​</a></h1><p>10 分钟的简易鸡蛋三明治 🥪</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>鸡蛋</li><li>吐司</li><li>培根</li><li>黄油</li><li>蛋黄酱</li><li>盐</li><li>黑胡椒</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>鸡蛋 1 个</li><li>吐司 2 片</li><li>培根 2 片</li><li>黄油 10 g</li><li>蛋黄酱 20g</li><li>盐 1g</li><li>黑胡椒 2g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>吐司切去四边，备用</li><li>鸡蛋煮熟，捣碎</li><li>混合鸡蛋、蛋黄酱、盐、黑胡椒</li><li>锅中加入黄油，煎熟培根</li><li>组装吐司，在两片吐司间加入制作好的鸡蛋酱及培根</li><li>四边形吐司切成三角形装盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>也可加入芝士、酸黄瓜等食材</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const p=a(r,[["render",o]]);export{c as __pageData,p as default};
