import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const _=JSON.parse('{"title":"葱油的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/condiment/葱油.md","filePath":"guide/condiment/葱油.md","lastUpdated":1747107234000}'),r={name:"guide/condiment/葱油.md"};function o(n,l,d,s,u,c){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="葱油的做法" tabindex="-1">葱油的做法 <a class="header-anchor" href="#葱油的做法" aria-label="Permalink to &quot;葱油的做法&quot;">​</a></h1><p>葱油是用热油萃取以葱为主的各类香辛料得到的产物，可以用来调制肉馅，做凉拌菜，在热炒菜中作为出锅明油使用。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>油</li><li>葱（大葱小葱都可以）</li><li>姜</li><li>洋葱</li><li>料酒</li><li>香菜（可选）</li><li>开洋（可选）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>油 200g</li><li>葱 80g</li><li>姜 20g</li><li>料酒 10ml</li><li>洋葱 150g</li><li>开洋 50g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>开洋泡入 50 度温水中，加入 10ml 料酒去腥，泡 10 分钟后取出沥干水分</li><li>葱，香菜洗净，切成 5cm 长的段，擦干表面水份</li><li>洋葱切成丝，在锅里用热水煮 5 分钟，取出沥干水份</li><li>姜去皮，切成片</li><li>锅里倒入全部油，放入上述预处理好的材料，开中小火炸 20 分钟</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>渣后的开洋葱渣也可以食用，葱油可以滤掉这些料渣，也可以保留</li><li>开洋可以极大提升葱油鲜香，并增加鲜甜风味</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const m=i(r,[["render",o]]);export{_ as __pageData,m as default};
