import{_ as e,c as l,o as i,ah as t}from"./chunks/framework.HzbgFGQH.js";const r="/assets/bottle.DZiZWTGy.jpg",m=JSON.parse('{"title":"蔗糖糖浆的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/condiment/蔗糖糖浆/蔗糖糖浆.md","filePath":"guide/condiment/蔗糖糖浆/蔗糖糖浆.md","lastUpdated":1747118079000}'),n={name:"guide/condiment/蔗糖糖浆/蔗糖糖浆.md"};function o(s,a,d,p,c,h){return i(),l("div",null,a[0]||(a[0]=[t('<h1 id="蔗糖糖浆的做法" tabindex="-1">蔗糖糖浆的做法 <a class="header-anchor" href="#蔗糖糖浆的做法" aria-label="Permalink to “蔗糖糖浆的做法”">​</a></h1><p>将糖事先溶解好便于在配制饮料（特别是冷饮）时给饮料增甜</p><p>预估烹饪难度：★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><p>原料</p><ul><li>白砂糖</li><li>水</li></ul><p>工具</p><ul><li>可密封容器（建议使用高硼硅试剂瓶，便宜）</li></ul><p><img src="'+r+'" alt="bottle"></p><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>建议比例=&gt;糖 : 水 = 1 : 1，这样方便进行糖量控制，比如想要 15 克糖，就量取 30 克的糖浆</p><ul><li>水 100 克</li><li>白砂糖 100 克</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ol><li>将称好的白砂糖的饮用水于容器混匀</li><li>容器封盖放冰箱冷藏</li></ol><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>水无论冷热都可，如果比较急用，可用热水溶解后再冷却糖浆。</p><p>配制好的糖浆不会轻易变质，在冷藏状态下保存一周通常是没问题的。</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',18)]))}const u=e(n,[["render",o]]);export{m as __pageData,u as default};
