import{_ as l,c as e,o as i,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E5%8F%8D%E6%B2%99%E8%8A%8B%E5%A4%B4%E6%88%90%E5%93%81.D-b1OeWj.jpg",u=JSON.parse('{"title":"反沙芋头的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/dessert/反沙芋头/反沙芋头.md","filePath":"guide/dessert/反沙芋头/反沙芋头.md","lastUpdated":1747108385000}'),s={name:"guide/dessert/反沙芋头/反沙芋头.md"};function d(n,a,o,h,_,c){return i(),e("div",null,a[0]||(a[0]=[r('<h1 id="反沙芋头的做法" tabindex="-1">反沙芋头的做法 <a class="header-anchor" href="#反沙芋头的做法" aria-label="Permalink to “反沙芋头的做法”">​</a></h1><p><img src="'+t+'" alt="反沙芋头成品"></p><p>反沙芋头是一道著名的潮汕小吃，下午茶，制作起来特别方便，～预计制作时间 20 分钟</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>荔浦芋头（电商平台购买即可，实惠新鲜）</li><li>白砂糖或冰糖</li><li>水</li><li>葱</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><ul><li>荔浦芋头 200g</li><li>白砂糖 30g</li><li>水 15g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>芋头切长条（稍微大条一点，翻炒过程不容易烂）</li><li>加入可以没过芋头的油，等油温起来（插入筷子冒小泡即可）</li><li>放进芋头到油里，去炸到芋头浮起来，一般是微微泛黄并且可以用筷子很轻松戳洞</li><li>炸芋头的油放起来别浪费，后面炒菜啥的都能用</li><li>接下来关键的一步，把糖(30g)和水(15g)按照 2：1 比例，加热至不变色且冒小泡</li><li>倒入葱花和芋头，关火翻炒，此时等温度下来，糖就会有反沙的效果</li><li>装盘上桌！</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>刚做好的反沙芋头很烫，小心烫嘴哦</li><li>再配上茶，啊~真惬意啊</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const m=l(s,[["render",d]]);export{u as __pageData,m as default};
