import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E8%8A%8B%E6%B3%A5%E9%9B%AA%E5%AA%9A%E5%A8%98%E6%88%90%E5%93%81.D8w65-Wz.jpg",c=JSON.parse('{"title":"芋泥雪媚娘的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/dessert/芋泥雪媚娘/芋泥雪媚娘.md","filePath":"guide/dessert/芋泥雪媚娘/芋泥雪媚娘.md","lastUpdated":1747034379000}'),s={name:"guide/dessert/芋泥雪媚娘/芋泥雪媚娘.md"};function o(d,l,n,g,u,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="芋泥雪媚娘的做法" tabindex="-1">芋泥雪媚娘的做法 <a class="header-anchor" href="#芋泥雪媚娘的做法" aria-label="Permalink to &quot;芋泥雪媚娘的做法&quot;">​</a></h1><p><img src="'+r+'" alt="芋泥雪媚娘成品"></p><p>芋泥雪媚娘是一道甜品，很适合做给孩子吃，无需烤箱，手残党也可以做成功～预计制作时间 2 小时。</p><p>预估烹饪难度：★★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>荔浦芋头（电商平台购买即可，实惠新鲜）</li><li>紫薯粉</li><li>牛奶</li><li>糯米粉</li><li>玉米淀粉</li><li>黄油</li><li>淡奶油</li><li>白砂糖</li><li>料理搅拌机（电动打蛋器也可以）</li><li>筛网</li><li>保鲜膜</li><li>白砂糖</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>荔浦芋头 200g</li><li>紫薯粉 3g</li><li>牛奶 165g</li><li>糯米粉 a 50g</li><li>糯米粉 b 75g</li><li>玉米淀粉 22g</li><li>黄油 30g</li><li>淡奶油（推荐安佳） 145g</li><li>白砂糖 26g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>芋头切块，大火煮熟至软（40 分钟即可），全部放入料理机</li><li>向内加入 30g 牛奶，25g 淡奶油，将其打成泥状</li><li>再向内加入 3g 紫薯粉，18g 白砂糖，继续搅拌打成细腻芋泥</li><li>取出另一个碗，加入全部糯米粉 b，22g 玉米淀粉，135g 牛奶，50g 白砂糖，混匀并过筛一遍，保鲜膜盖上并扎小洞，中火蒸半个小时</li><li>在蒸的过程中，将糯米粉 a 放入平底锅小火翻炒至微微发黄（即炒熟），作为手粉备用</li><li>将中火蒸完半小时的糯米牛奶混合物（果冻状）趁热加入黄油 30g，将黄油揉至面团完全吸收，然后放冰箱冷藏一小时</li><li>取出另一只碗，加入 120g 淡奶油，8g 白砂糖，打发至有纹路，装进裱花袋备用</li><li>取出冷藏后的面团，搓揉 5 分钟，分成 30g 一个，均匀撒上 2g 手粉防粘，擀成圆形，先挤上 5g 裱花奶油，然后放上 30g 芋泥，最后将面饼像包包子一样包起来（可以减去多余的皮）</li><li>包好后再均匀撒 2g 手粉防粘</li><li>重复以上两步直至原材料用光</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>制作时最好佩戴不粘手套</li><li>制作完即可食用，不用再加热</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=i(s,[["render",o]]);export{c as __pageData,p as default};
