import{_ as i,c as a,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E8%8B%B1%E5%BC%8F%E5%8F%B8%E5%BA%B7.xgzG0HHH.png",u=JSON.parse('{"title":"英式司康的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/dessert/英式司康/英式司康.md","filePath":"guide/dessert/英式司康/英式司康.md","lastUpdated":1747118079000}'),s={name:"guide/dessert/英式司康/英式司康.md"};function d(n,l,o,h,_,c){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="英式司康的做法" tabindex="-1">英式司康的做法 <a class="header-anchor" href="#英式司康的做法" aria-label="Permalink to “英式司康的做法”">​</a></h1><p><img src="'+t+'" alt="示例菜成品"></p><p>英式司康是非常简单快手的下午茶甜品，可以搭配果酱、茶与咖啡。成品以蛋奶香气为主轴风味，糖量适中不会过于甜腻。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>无盐黄油（推荐品牌总统）</li><li>低筋面粉</li><li>糖</li><li>盐</li><li>泡打粉</li><li>鸡蛋</li><li>淡奶油</li><li>奶油奶酪（可选）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 4-6 个人吃。</p><p>每份：</p><ul><li>无盐黄油 40g</li><li>低筋面粉 180g</li><li>糖 30g</li><li>盐 1g</li><li>泡打粉 5g</li><li>鸡蛋 1 个（约 50g）</li><li>淡奶油 45g</li><li>奶油奶酪 50g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>鸡蛋打散，称量出 30g 蛋液放入干净容器中，放入全量淡奶油和奶油奶酪混合均匀。如果奶酪太硬可以水浴加热至大约 40 度再混合。</li><li>将低筋面粉，盐，糖，泡打粉放入干净容器中混合均匀</li><li>黄油切成小块，放入上一步的混合物中，用手将黄油捏入混合物中，呈粗玉米粉质地</li><li>将第一步的蛋奶混合液倒入上一步得到的粉油混合物种，搅拌均接近。叠压成均匀面团</li><li>面团放到案板上，擀成 1.5cm 厚的面片，用刀或者模具分切成合适的形状</li><li>用刷子蘸取剩余的 20g 鸡蛋液，刷在司康表面</li><li>烤箱预热 180 度，烤制 27 分钟</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>最简单的分切方案是擀成圆形面皮，用刀切成 6 个扇形</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',15)]))}const g=i(s,[["render",d]]);export{u as __pageData,g as default};
