import{_ as a,c as i,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E9%9B%AA%E8%8A%B1%E9%85%A5%E6%88%90%E5%93%81.CXglPcge.jpg",u=JSON.parse('{"title":"雪花酥的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/dessert/雪花酥/雪花酥.md","filePath":"guide/dessert/雪花酥/雪花酥.md","lastUpdated":1747118079000}'),s={name:"guide/dessert/雪花酥/雪花酥.md"};function d(h,l,n,o,c,_){return e(),i("div",null,l[0]||(l[0]=[r('<h1 id="雪花酥的做法" tabindex="-1">雪花酥的做法 <a class="header-anchor" href="#雪花酥的做法" aria-label="Permalink to “雪花酥的做法”">​</a></h1><p><img src="'+t+'" alt="雪花酥成品"></p><p>雪花酥是一个快捷简便的甜点，适合装盒送礼，制作耗时 30 分钟。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><h3 id="工具" tabindex="-1">工具 <a class="header-anchor" href="#工具" aria-label="Permalink to “工具”">​</a></h3><ul><li>不粘平底锅</li><li>烘焙刮刀（可用锅铲代替，小心使用破坏不粘锅涂层）</li><li>一次性塑料手套</li><li>雪花酥/牛轧糖模具</li><li>擀面杖</li></ul><h3 id="原料" tabindex="-1">原料 <a class="header-anchor" href="#原料" aria-label="Permalink to “原料”">​</a></h3><ul><li>无盐黄油</li><li>棉花糖</li><li>全脂奶粉</li><li>混合坚果（三只松鼠每日坚果）</li><li>饼干（非夹心型饼干，推荐小奇福或购买使用雪花酥烘焙专用小饼干）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>无盐黄油 20g</li><li>棉花糖 75g</li><li>全脂奶粉 40g</li><li>混合坚果 60g</li><li>饼干 75g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>饼干超过一元硬币大小先切成小块</li><li>无盐黄油加入锅中，小火加热至无盐黄油完全融化</li><li>棉花糖加入锅中，使用刮刀搅拌，直至棉花糖融化并与无盐黄油均匀融合</li><li>20g 奶粉加入锅中，使用刮刀搅拌，奶粉与黄油棉花糖混合物搅拌均匀后立即关火</li><li>准备好的所有混合坚果与饼干趁热加入锅中，使用刮刀搅拌</li><li>搅拌到温度下降到手可以接触的温度后，戴上一次塑料手套，在锅中搓揉或者双手拿起拉扯，让饼干混合坚果与棉花糖黄油奶粉混合物分散均匀。</li><li>将上述步骤混合物压入模具中，边角压实，擀面杖擀平，未满的一边用手尽量压成直边</li><li>室温放凉，完全冷却后脱模，按照模具纹路切块，或切成自己喜欢的大小，撒上剩余奶粉，尽量使雪花酥每面都沾上奶粉</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>棉花糖加热时间越长，最后成品越硬</li><li>参考资料：<a href="https://b23.tv/P547ILO" target="_blank" rel="noreferrer">【一口小锅 十分钟搞定的网红草莓“雪花酥”-哔哩哔哩】</a></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',17)]))}const m=a(s,[["render",d]]);export{u as __pageData,m as default};
