import{_ as l,c as e,o as i,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E9%BE%9F%E8%8B%93%E8%86%8F%E6%88%90%E5%93%81.C6CkECNQ.jpg",p=JSON.parse('{"title":"龟苓膏的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/dessert/龟苓膏/龟苓膏.md","filePath":"guide/dessert/龟苓膏/龟苓膏.md","lastUpdated":1747107234000}'),s={name:"guide/dessert/龟苓膏/龟苓膏.md"};function o(d,a,h,n,_,c){return i(),e("div",null,a[0]||(a[0]=[t('<h1 id="龟苓膏的做法" tabindex="-1">龟苓膏的做法 <a class="header-anchor" href="#龟苓膏的做法" aria-label="Permalink to &quot;龟苓膏的做法&quot;">​</a></h1><p><img src="'+r+'" alt="龟苓膏成品"></p><p>预估烹饪难度：★★</p><hr><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>龟苓膏粉 25 克</li><li>冷水 120 毫升</li><li>开水 500 毫升</li><li>白砂糖 100 克</li><li>小锅</li><li>搅拌工具</li><li>模具或碗</li></ul><hr><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作约可获得 3~4 小碗成品，适合 2~4 人食用。</p><hr><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ol><li>在锅中倒入龟苓膏粉 25 克与冷水 120 毫升，充分搅拌至无颗粒感。</li><li>在另一个容器中加入白砂糖 100 克，倒入沸水 500 毫升，搅拌至糖完全溶解。</li><li>将糖水缓慢倒入龟苓膏粉液中，立即搅拌均匀，避免结块。</li><li>将混合液体放入锅中，加热时保持中小火，并持续搅拌以防粘锅。</li><li>加热至液体变粘稠并开始冒小泡，即可关火。</li><li>快速将液体倒入模具中，自然冷却凝固。建议冷藏 1~2 小时后食用，口感更佳。</li></ol><hr><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>龟苓膏冷却过程中不可随意移动容器，否则可能影响成型。</li><li>加热时应避免使用大火，防止结块或糊底。</li><li>可根据个人口味添加蜂蜜、炼乳或水果食用。</li><li>不同品牌龟苓膏粉浓度略有差异，建议参照包装说明调整配比。</li></ul><hr><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',17)]))}const m=l(s,[["render",o]]);export{p as __pageData,m as default};
