import{_ as l,c as a,o as e,ah as t}from"./chunks/framework.HzbgFGQH.js";const p=JSON.parse('{"title":"Mojito莫吉托的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/drink/Mojito莫吉托.md","filePath":"guide/drink/Mojito莫吉托.md","lastUpdated":1747108270000}'),r={name:"guide/drink/Mojito莫吉托.md"};function o(n,i,d,s,h,_){return e(),a("div",null,i[0]||(i[0]=[t('<h1 id="mojito莫吉托的做法" tabindex="-1">Mojito莫吉托的做法 <a class="header-anchor" href="#mojito莫吉托的做法" aria-label="Permalink to “Mojito莫吉托的做法”">​</a></h1><p>Mojito 是一种传统的古巴高球鸡尾酒。</p><p>这种调酒有着相对低的酒精含量（大约 10%）。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>打碎的冰块</li><li>冰镇苏打水</li><li>压汁器</li><li>海波杯</li><li>研杵</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>一块青柠（切成两个半块）</li><li>五珠薄荷叶</li><li>糖浆 20ml</li><li>金色朗姆酒 45ml</li><li>蓝天原味伏特加 10ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>将切成半块的青柠之一切成小块，放入海波杯，随后用研杵将其捣出汁；</li><li>用 3-4 珠薄荷叶沿着杯口涂抹，随后将其放入杯中；</li><li>加入 糖浆 20ml；</li><li>加入 金色朗姆酒 45ml；</li><li>将剩下的半块青柠压出汁水放入杯中；</li><li>轻轻搅拌，使砂糖/糖浆处于半融合状态；</li><li>将打碎的冰块放入杯中，直到占杯中 3/4；</li><li>加入冰镇苏打水直到刚好淹没碎冰；</li><li>旋转搅拌半分钟；</li><li>使用碎冰将海波杯补满；</li><li>将剩下的一株薄荷叶拍醒，插入碎冰，作装饰。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>参考资料：<a href="https://www.bilibili.com/video/BV1jg4y187kB" target="_blank" rel="noreferrer">【哔哩哔哩】莫吉托(Mojito)世界上最著名的经典鸡尾酒之一。</a></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=l(r,[["render",o]]);export{p as __pageData,m as default};
