import{_ as a,c as i,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const c=JSON.parse('{"title":"可乐桶的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/drink/可乐桶.md","filePath":"guide/drink/可乐桶.md","lastUpdated":1747118079000}'),t={name:"guide/drink/可乐桶.md"};function n(o,l,s,d,p,h){return e(),i("div",null,l[0]||(l[0]=[r('<h1 id="可乐桶的做法" tabindex="-1">可乐桶的做法 <a class="header-anchor" href="#可乐桶的做法" aria-label="Permalink to “可乐桶的做法”">​</a></h1><p><strong>饮酒有害健康，未成年人禁止饮酒</strong></p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><p>原料</p><ul><li>波旁威士忌</li><li>可口可乐</li><li>冰块</li><li>柠檬（可选，提升口感用）</li></ul><p>工具</p><ul><li>手动压汁器</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>一杯分量，约 900 毫升（含冰）</p><ul><li>威士忌 100 毫升</li><li>可口可乐 500 毫升</li><li>柠檬 1 个</li><li>冰块 300 克</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><p>柠檬的处理</p><ol><li>将柠檬对半切（<strong>刀方向垂直于柠檬的头尾连线</strong>），并从其中的一半中切取一片柠檬备用</li><li>再次将柠檬对半切，将得到的 4 角柠檬用压汁器压出柠檬汁置于容器中备用</li><li>将挤压过的柠檬置于容器中备用</li></ol><p>正式调配</p><ol><li>选择一个杯子，建议使用容量在大约 1 升的大型玻璃杯或铁皮酒桶</li><li>将冰块和挤压过的柠檬入杯中（可根据个人喜好设计柠檬与冰块的摆放）</li><li>倒入 15 毫升柠檬汁（如果喜酸可以加多点或全加）</li><li>沿杯壁缓慢倒入可口可乐至距离杯口 3/4 处（控制可乐和威士忌的比例约在 5:1 ）</li><li>倒入威士忌直至满杯</li></ol><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>可乐桶因冰块和可乐的口感掩盖了威士忌的酒味，因此不善酒力的人也容易在不知不觉中过量饮酒，请在保证个人与饮酒者的安全下调配。</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',19)]))}const u=a(t,[["render",n]]);export{c as __pageData,u as default};
