import{_ as i,c as a,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/kiwi-example.H94fc_2x.jpg",p=JSON.parse('{"title":"奇异果菠菜特调的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/drink/奇异果菠菜特调/奇异果菠菜特调.md","filePath":"guide/drink/奇异果菠菜特调/奇异果菠菜特调.md","lastUpdated":1747108385000}'),s={name:"guide/drink/奇异果菠菜特调/奇异果菠菜特调.md"};function n(d,l,h,o,_,c){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="奇异果菠菜特调的做法" tabindex="-1">奇异果菠菜特调的做法 <a class="header-anchor" href="#奇异果菠菜特调的做法" aria-label="Permalink to “奇异果菠菜特调的做法”">​</a></h1><p>预估烹饪难度：★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>原料: <ul><li>奇异果</li><li>苹果</li><li>菠菜叶（ 2-5 片）</li><li>水</li><li>白砂糖</li></ul></li><li>工具 <ul><li>榨汁机</li></ul></li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><ul><li>饮用水 700ml</li><li>奇异果 2 个</li><li>苹果 1/2 个</li><li>菠菜叶 4 叶</li><li>白砂糖 12 克</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>将猕猴桃切成两半，每半再分四份小块</li><li>将苹果切丁</li><li>将菠菜叶去梗，只留叶子部分</li><li>将菠菜切碎</li><li>一起倒入榨汁机搅拌杯</li><li>注水</li><li>加入白砂糖</li><li>启动搅拌机，搅拌约 4 个 15 秒（每 15 秒停下看状态）</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>关于白砂糖的量：全糖为 12g，可根据个人情况在 0-12g 中调整</li><li>本品是仿照宜家的奇异果菠菜汁自己研制的，没有宜家的好喝，但也不错</li><li>欢迎改进！</li></ul><h3 id="成品" tabindex="-1">成品 <a class="header-anchor" href="#成品" aria-label="Permalink to “成品”">​</a></h3><p><img src="'+t+'" alt="奇异果菠菜特调"></p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const m=i(s,[["render",n]]);export{p as __pageData,m as default};
