import{_ as e,c as l,o as i,ag as t}from"./chunks/framework.DPDPlp3K.js";const _=JSON.parse('{"title":"奶茶的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/drink/奶茶.md","filePath":"guide/drink/奶茶.md","lastUpdated":1747107234000}'),r={name:"guide/drink/奶茶.md"};function o(n,a,s,d,u,h){return i(),l("div",null,a[0]||(a[0]=[t('<h1 id="奶茶的做法" tabindex="-1">奶茶的做法 <a class="header-anchor" href="#奶茶的做法" aria-label="Permalink to &quot;奶茶的做法&quot;">​</a></h1><p>奶茶是一种简单易做的饮料。一般初学者只需要 30 分钟即可完成。</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>袋泡红茶（推荐立顿黄牌精选红茶）</li><li>全脂奶粉或淡奶</li><li>杯子，例如带刻度的杯子，陶瓷杯或保温杯</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>袋泡红茶 2 包（约 4g）</li><li>奶粉 11-12g</li><li>砂糖 5-7g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>取袋泡红茶 2 包放入杯中，加入 180-200mL <strong>沸水</strong>。</li><li><strong>等待 20 - 30 分钟</strong>。</li><li>称取 11-12g 奶粉和 5-7g 砂糖，分别加入前一步骤得到的液体中。</li><li>搅拌均匀即可饮用。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>加入沸水后，尽量保持杯子内部温暖，例如使用开口较小的杯子或盖上盖子。如果气温较低时，使用量杯量取可能导致沸水在冲入红茶前冷却，则可以不使用量杯量取而直接估计其体积。</li><li>等待时间结束后可提起或搅动茶包以使冲泡更加均匀。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=e(r,[["render",o]]);export{_ as __pageData,p as default};
