import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E6%B3%B0%E5%9B%BD%E6%89%8B%E6%A0%87%E7%BA%A2%E8%8C%B6.BQ8HVX9T.jpg",p=JSON.parse('{"title":"泰国手标红茶的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/drink/泰国手标红茶/泰国手标红茶.md","filePath":"guide/drink/泰国手标红茶/泰国手标红茶.md","lastUpdated":1747107234000}'),o={name:"guide/drink/泰国手标红茶/泰国手标红茶.md"};function s(n,l,d,c,_,h){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="泰国手标红茶的做法" tabindex="-1">泰国手标红茶的做法 <a class="header-anchor" href="#泰国手标红茶的做法" aria-label="Permalink to &quot;泰国手标红茶的做法&quot;">​</a></h1><p><img src="'+r+'" alt="泰国手标红茶成品"></p><p>泰国手标红茶是泰国街头随处可见的奶茶，味道香纯，绵密。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>水</li><li>茶粉</li><li>炼乳</li><li>白糖</li><li>牛奶</li><li>克称</li><li>带刻度容器</li><li>港式奶茶过滤袋</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 1 个人吃。</p><p>每份：</p><ul><li>水(600cc)</li><li>茶粉(20g)</li><li>白糖(24g)</li><li>牛奶(18ml)</li><li>炼乳(24g)</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>600cc 水大火烧开</li><li>在过滤袋中装入 20g 茶粉，开水倒入过滤袋中，过滤 20 遍</li><li>使用克称量取 24g 炼乳、24g 白糖和 18ml 牛奶放入 1000ml 以上的水壶中</li><li>将过滤好的茶水倒入水壶中搅拌，直到白糖融化</li><li>将水壶放到冰箱 4 小时以上</li><li>喝前可以加 6-8 颗冰块</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=i(o,[["render",s]]);export{p as __pageData,m as default};
