import{_ as l,c as e,o as i,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E7%A0%82%E7%B3%96%E6%A4%B0%E5%AD%90%E5%86%B0%E6%B2%99-1.C5sAGtxt.jpg",c=JSON.parse('{"title":"砂糖椰子冰沙的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/drink/砂糖椰子冰沙/砂糖椰子冰沙.md","filePath":"guide/drink/砂糖椰子冰沙/砂糖椰子冰沙.md","lastUpdated":1747107234000}'),o={name:"guide/drink/砂糖椰子冰沙/砂糖椰子冰沙.md"};function s(n,a,d,u,_,h){return i(),e("div",null,a[0]||(a[0]=[t('<h1 id="砂糖椰子冰沙的做法" tabindex="-1">砂糖椰子冰沙的做法 <a class="header-anchor" href="#砂糖椰子冰沙的做法" aria-label="Permalink to &quot;砂糖椰子冰沙的做法&quot;">​</a></h1><p><img src="'+r+'" alt="alt text"></p><p>砂糖椰子冰沙是一种制作极其快速方便的饮料，若原料选择得当则口感丰富。然而制作时动静较大，适合白天在家制作以作为下午茶。</p><p>预估烹饪难度：★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>瓶装椰汁（瓶口较大为佳）</li><li>咖啡调糖（黄色粗粒）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>冰沙最佳份量通常与融化速度有关，300g 在零下 18 度制成的冰沙在室温下通常可维持 30 分钟。</p><p>每份：</p><ul><li>瓶装椰子汁 500ml</li><li>咖啡调糖 10g（两包太古咖啡调糖）</li><li>坚果碎（可选）</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>500ml 瓶装椰汁倒掉 200ml，立刻拧紧瓶盖。</li><li>将这瓶椰汁放入冰箱冷冻区并冷冻 10 小时以上。</li><li>将这瓶椰汁取出，若确认瓶中椰汁已彻底冻结，则在墙角、椅背、桌角等坚硬表面上用力抽打。（请务必确认表面不会因此受到损伤）</li><li>当抽打到冻结椰汁变成冰沙状态，打开瓶盖倒出冰沙。</li><li>在冰沙表面均匀撒上咖啡调糖或坚果碎。</li><li>完成</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>在以上步骤中，瓶装椰汁可以提前冰冻，但是不宜超过 7 天，否则有变质风险。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',15)]))}const m=l(o,[["render",s]]);export{c as __pageData,m as default};
