import{_ as i,c as a,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E8%8F%A0%E8%90%9D%E5%92%96%E5%95%A1%E7%89%B9%E8%B0%83.GI2JWHBD.png",m=JSON.parse('{"title":"菠萝咖啡特调的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/drink/菠萝咖啡特调/菠萝咖啡特调.md","filePath":"guide/drink/菠萝咖啡特调/菠萝咖啡特调.md","lastUpdated":1747118079000}'),n={name:"guide/drink/菠萝咖啡特调/菠萝咖啡特调.md"};function s(d,l,o,_,h,c){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="菠萝咖啡特调的做法" tabindex="-1">菠萝咖啡特调的做法 <a class="header-anchor" href="#菠萝咖啡特调的做法" aria-label="Permalink to “菠萝咖啡特调的做法”">​</a></h1><p><img src="'+t+'" alt="示例菜成品"></p><p>菠萝咖啡特调是非常适合家庭出品的饮料，酸甜可口。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>咖啡液（推荐浓缩或者冷萃）</li><li>菠萝汁（鲜榨或者 nfc）</li><li>冰块</li><li>苏打水</li><li>奶油</li><li>牛奶</li><li>糖</li><li>海盐（可选）</li><li>朗姆酒 （可选）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 1 个人吃。</p><p>每份：</p><ul><li>咖啡液 30ml</li><li>菠萝汁 60ml</li><li>冰块 50g</li><li>苏打水 30ml</li><li>奶油 30ml</li><li>牛奶 10ml</li><li>糖 8g</li><li>海盐 0.5g</li><li>朗姆酒 5ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>杯子里依次加入冰块，咖啡液，菠萝汁和苏打水</li><li>奶油加糖打发至湿性发泡，加入朗姆酒和牛奶均匀只有流动性</li><li>在第一部混合液上方倒入奶油</li><li>奶油顶面撒上海盐</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>咖啡液建议选用花果风味，浓缩可以用中烘豆，冷萃可以用普通水洗或者日晒</li><li>倒入奶油时，高度尽量放低，让奶油能均匀漂浮在饮料表面形成奶盖</li><li>用新鲜的咖啡液和菠萝汁能大大提升整体风味口感</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',15)]))}const u=i(n,[["render",s]]);export{m as __pageData,u as default};
