import{_ as l,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/sour_plum_soup.Dv4-ipAe.jpg",c=JSON.parse('{"title":"酸梅汤的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/drink/酸梅汤/酸梅汤.md","filePath":"guide/drink/酸梅汤/酸梅汤.md","lastUpdated":1747107234000}'),o={name:"guide/drink/酸梅汤/酸梅汤.md"};function s(n,i,d,h,p,u){return e(),a("div",null,i[0]||(i[0]=[t('<h1 id="酸梅汤的做法" tabindex="-1">酸梅汤的做法 <a class="header-anchor" href="#酸梅汤的做法" aria-label="Permalink to &quot;酸梅汤的做法&quot;">​</a></h1><p><img src="'+r+'" alt="酸梅汤"></p><p>视频演示： <a href="https://www.bilibili.com/video/BV1164y1F7hv/" target="_blank" rel="noreferrer">链接</a></p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>水</li><li>乌枣</li><li>乌梅</li><li>山楂片（生）</li><li>黄冰糖</li><li>甘草</li><li>陈皮</li><li>红豆蔻</li><li>干桂花</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>两升水</li><li>乌枣 25 克</li><li>乌梅 25g</li><li>黄冰糖 100 克</li><li>山楂片 30 克</li><li>甘草 2 克</li><li>陈皮 4 克</li><li>红豆蔻 1 克</li><li>干桂花 3 克</li></ul><p>上述所有用量可以等比例增加或减小。</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>冲洗材料（干桂花和冰糖除外）， 1.5 升水常温浸泡两小时以上（干桂花和冰糖除外）</li><li>开中大火煮沸，盖盖，转小火煮 40 分钟，为头煎</li><li>将冰糖放入盆内，再将沥好用材的头汤趁热倒入，搅拌至冰糖融化。</li><li>药材重新装回锅内再 600 毫升的水，开大火煮沸，盖盖，转中火，再煮 20 分钟为二煎</li><li>最后将二煎和冰糖水趁热混合为成品。在成品 60-70℃加入干桂花（不要超过 80℃）加盖晾凉再放入冰箱冷藏 3 小时以上。</li><li>饮用时记得将干桂花沥出。如饮茶般细啜，冰凉振齿，酸醒人、甜适度，滋味丰满而悠长</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>放入冰箱冷藏后再饮用效果更佳。</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=l(o,[["render",s]]);export{c as __pageData,m as default};
