import{_ as l,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/gin-tonic.yzmiOhAj.jpg",_=JSON.parse('{"title":"金汤力的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/drink/金汤力/金汤力.md","filePath":"guide/drink/金汤力/金汤力.md","lastUpdated":1747107234000}'),o={name:"guide/drink/金汤力/金汤力.md"};function n(s,i,d,h,p,c){return e(),a("div",null,i[0]||(i[0]=[t('<h1 id="金汤力的做法" tabindex="-1">金汤力的做法 <a class="header-anchor" href="#金汤力的做法" aria-label="Permalink to &quot;金汤力的做法&quot;">​</a></h1><p><strong>饮酒有害健康，未成年人禁止饮酒</strong></p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><p>原料</p><ul><li>金酒</li><li>汤力水气泡水</li><li>柠檬</li><li>冰块</li><li>新鲜绿叶（可选，装饰用）</li></ul><p>工具</p><ul><li>手动压汁器</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>一杯分量，约 350 毫升（含冰）</p><ul><li>金酒 30~40 毫升</li><li>汤力水气泡水 1 罐</li><li>柠檬 1 个</li><li>冰块 100 克</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><p>柠檬的处理</p><ol><li>将柠檬对半切（<strong>刀方向垂直于柠檬的头尾连线</strong>），并从其中的一半中切取一片柠檬备用</li><li>再次将柠檬对半切，将得到的 4 角柠檬用压汁器压出柠檬汁置于容器中备用</li></ol><p>正式调配</p><ol><li>选择一个杯子，建议使用容量在 350~400 毫升的透明玻璃杯</li><li>将 100 克冰块放置在杯底</li><li>倒入 30~40 毫升金酒</li><li>倒入 15 毫升柠檬汁（如果喜酸可以加多点或全加）</li><li>用勺子搅拌均匀</li><li>将之前准备的一片柠檬放置好</li><li>缓慢沿杯壁注入汤力水直至满杯（不要倒在冰上，避免起泡流失）</li><li>用勺子轻轻上下提拉将液体搅拌均匀（不要旋转搅拌，避免起泡流失）</li><li>在液面放置好装饰用的绿叶（可选）</li></ol><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><h3 id="成品" tabindex="-1">成品 <a class="header-anchor" href="#成品" aria-label="Permalink to &quot;成品&quot;">​</a></h3><p><img src="'+r+'" alt="gin-tonic"></p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',20)]))}const m=l(o,[["render",n]]);export{_ as __pageData,m as default};
