import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const s="/assets/%E5%86%AC%E7%93%9C%E9%85%BF%E8%82%89%E6%88%90%E5%93%81.CHTlh_07.jpg",r="/assets/%E5%86%AC%E7%93%9C%E5%BD%A2%E7%8A%B6.jAiRv1Jz.jpg",o="/assets/%E8%85%8C%E5%88%B6%E5%A5%BD%E7%9A%84%E5%86%AC%E7%93%9C.CBInAl5Z.jpg",p="/assets/%E5%8D%B7%E8%82%89.Dj3OYMsX.jpg",_="/assets/%E6%91%86%E7%9B%98.D91VZt0g.jpg",c="/assets/%E6%89%93%E9%B8%A1%E8%9B%8B.Dx5QfThk.jpg",b=JSON.parse('{"title":"冬瓜酿肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/冬瓜酿肉/冬瓜酿肉.md","filePath":"guide/meat_dish/冬瓜酿肉/冬瓜酿肉.md","lastUpdated":1747034379000}'),d={name:"guide/meat_dish/冬瓜酿肉/冬瓜酿肉.md"};function n(u,l,m,h,g,E){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="冬瓜酿肉的做法" tabindex="-1">冬瓜酿肉的做法 <a class="header-anchor" href="#冬瓜酿肉的做法" aria-label="Permalink to &quot;冬瓜酿肉的做法&quot;">​</a></h1><p><img src="'+s+'" alt="冬瓜酿肉成品"></p><p>荤素搭配，鲜嫩爽滑,做法简单。一般 30 分钟。</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>冬瓜</li><li>猪肉末</li><li>鸡蛋</li><li>葱</li><li>葱姜末</li><li>胡椒粉</li><li>生抽</li><li>淀粉</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 2 个人吃。</p><p>每份：</p><ul><li>猪肉末 300g</li><li>鸡蛋 1 个（可选,不习惯的人可能会有点腥）</li><li>冬瓜 200g</li><li>葱花（一根,约 20g）</li><li>胡椒粉 5g</li><li>生抽 10ml</li><li>淀粉 5g</li><li>水淀粉 25g（淀粉 25g,水 50ml）</li><li>葱姜末（姜 3-4 片约 30g, 取上面一根葱花中的葱白部分即可）</li><li>盐 20g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>冬瓜去皮，切成 25cm 长 3cm 厚的片</li></ul><p><img src="'+r+'" alt="冬瓜形状"></p><ul><li>将切好的冬瓜放入碗中，放入 15g 盐，将冬瓜抹匀，放置 10 分钟</li><li>放置冬瓜的同时，换个碗放入肉末，葱姜末， 5g 盐，淀粉 5g，胡椒粉，生抽，胡椒粉</li><li>使用筷子在肉末中进行顺时针搅拌，搅拌到食材颜色没有明显对比（约 2 分钟）</li><li>将腌制好的冬瓜（会变软）使用清水洗三遍</li></ul><p><img src="'+o+'" alt="腌制好的冬瓜"></p><ul><li>拿出 1 片冬瓜片卷起来，并把肉塞进去</li></ul><p><img src="'+p+'" alt="卷肉"></p><ul><li>放入碟子中摆到碟子的边缘</li></ul><p><img src="'+_+'" alt="摆盘"></p><ul><li>打入 1 个鸡蛋到中间圆圈处</li></ul><p><img src="'+c+'" alt="打鸡蛋"></p><ul><li>放入普通铁锅中水烧开后，蒸 15 分钟，盖上锅盖</li><li>开盖，取出蒸好的冬瓜酿肉</li><li>将冬瓜酿肉碟子的水倒入锅中，放入水淀粉，加入 50ml 清水倒入锅中烧开</li><li>淋到冬瓜酿肉上</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>操作时，拿出冬瓜酿肉时注意碟子很烫，小心操作。</li><li>参考资料：<a href="https://www.bilibili.com/video/BV1oF411F7wD?spm_id_from=333.337.search-card.all.click&amp;vd_source=9f568660d497311d3f945e5dce319705" target="_blank" rel="noreferrer">b站视频</a></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',25)]))}const B=i(d,[["render",n]]);export{b as __pageData,B as default};
