import{_ as i,c as a,o as e,ah as t}from"./chunks/framework.HzbgFGQH.js";const s="/assets/%E5%87%89%E6%8B%8C%E9%B8%A1%E4%B8%9D.CBytmYch.jpg",r="/assets/%E5%87%89%E6%8B%8C%E9%B8%A1%E4%B8%9D_%E7%84%AF%E6%B0%B4.TppuwfRH.jpg",o="/assets/%E5%87%89%E6%8B%8C%E9%B8%A1%E4%B8%9D_%E6%92%95.loIzIfy4.jpg",E=JSON.parse('{"title":"凉拌鸡丝的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/凉拌鸡丝/凉拌鸡丝.md","filePath":"guide/meat_dish/凉拌鸡丝/凉拌鸡丝.md","lastUpdated":1747108270000}'),_={name:"guide/meat_dish/凉拌鸡丝/凉拌鸡丝.md"};function n(d,l,h,p,c,m){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="凉拌鸡丝的做法" tabindex="-1">凉拌鸡丝的做法 <a class="header-anchor" href="#凉拌鸡丝的做法" aria-label="Permalink to “凉拌鸡丝的做法”">​</a></h1><p><img src="'+s+'" alt="凉拌鸡丝成品"> 隔离期间的一道快手菜，少油低卡，制作简单，预计制作时间 30 分钟</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>鸡胸肉（常温冷冻均可）</li><li>麻油（花椒油）</li><li>生抽</li><li>香醋</li><li>白糖</li><li>盐</li><li>料酒</li><li>姜</li><li>凉白开水</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>鸡胸肉 200 克</li><li>麻油 5 毫升</li><li>生抽 4 毫升</li><li>香醋 4 毫升</li><li>白糖 3 克</li><li>盐 2 克</li><li>姜 20 克</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>姜切片，备用</li><li>锅中倒入 4 升水</li><li>加入鸡胸肉、姜片</li><li>倒入 20 毫升料酒</li><li>开大火不盖盖将水烧开</li><li>水开后转中火，用勺子将浮沫捞出</li><li>继续煮 <strong>5-7</strong> 分钟，如果是非冷冻肉煮 5 分钟，冷冻肉煮 7 分钟</li><li>鸡胸肉大小会影响成熟时间，用筷子插入鸡胸肉，如果能轻松插入，代表鸡肉熟了。如果不熟需延长煮制时间</li></ul><p><img src="'+r+'" alt="凉拌鸡丝焯水"></p><ul><li>用凉白开水冲泡鸡胸肉，使鸡胸肉降至室温</li><li>顺着鸡胸肉纹理将鸡胸肉撕成细丝</li></ul><p><img src="'+o+'" alt="凉拌鸡丝_细丝"></p><ul><li>准备一个碗</li><li>碗中加入准备好的麻油、生抽、香醋、白糖、盐</li><li>搅拌料汁，使糖和盐尽量溶化</li><li>将料汁倒入鸡丝中，搅拌均匀</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>这道菜是隔离期间用现有材料做的，有条件可以加入香菜、小葱等进行调味，喜欢吃辣的可以加入辣椒油</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',17)]))}const f=i(_,[["render",n]]);export{E as __pageData,f as default};
