import{_ as a,c as i,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const p=JSON.parse('{"title":"姜葱捞鸡的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/姜葱捞鸡/姜葱捞鸡.md","filePath":"guide/meat_dish/姜葱捞鸡/姜葱捞鸡.md","lastUpdated":1747118079000}'),t={name:"guide/meat_dish/姜葱捞鸡/姜葱捞鸡.md"};function d(s,l,h,n,o,_){return e(),i("div",null,l[0]||(l[0]=[r('<h1 id="姜葱捞鸡的做法" tabindex="-1">姜葱捞鸡的做法 <a class="header-anchor" href="#姜葱捞鸡的做法" aria-label="Permalink to “姜葱捞鸡的做法”">​</a></h1><p>嫩滑爆汁，白饭杀手，简单易做，</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>鸡腿肉</li><li>盐焗鸡粉</li><li>葱，姜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 2 个人吃。</p><p>每份：</p><ul><li>鸡腿 4 个， 约 400g</li><li>盐焗鸡粉 5g</li><li>姜 50g</li><li>葱 1 根</li><li>盐 5g</li><li>糖 5g</li><li>油 35ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>四个鸡腿清洗干净，放入碗中</li><li>碗中加入盐焗鸡粉和 5ml 油，搅拌均匀</li><li>让鸡腿静置腌制 15 分钟， 同时准备蒸锅并把水煮开</li><li>鸡腿腌制完成后， 放入水开后的蒸锅中，蒸制 20 分钟</li><li>将姜根据个人口味切成 1）姜蓉或 2）姜丝或 3）姜粒</li><li>将葱切成 0.5cm 小段</li><li>将葱姜放入蘸料碗，并加入盐和糖</li><li>将剩余的油倒入另一个锅中加热至六至七层热</li><li>将热油淋入葱姜碗中</li><li>鸡腿蒸好后将其撕碎成鸡丝，不需要特别细，大概 1cm 粗就可以</li><li>姜葱姜油淋在鸡丝上，搅拌均匀即可</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>参考: <a href="https://b23.tv/2trBdqJ" target="_blank" rel="noreferrer">白饭杀手来啦 #姜葱捞鸡-哔哩哔哩</a></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const m=a(t,[["render",d]]);export{p as __pageData,m as default};
