import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"小炒肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/小炒肉.md","filePath":"guide/meat_dish/小炒肉.md","lastUpdated":1747107234000}'),r={name:"guide/meat_dish/小炒肉.md"};function o(d,l,s,n,h,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="小炒肉的做法" tabindex="-1">小炒肉的做法 <a class="header-anchor" href="#小炒肉的做法" aria-label="Permalink to &quot;小炒肉的做法&quot;">​</a></h1><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>五花肉</li><li>朝天椒</li><li>小米椒</li><li>豆豉</li><li>豆瓣酱</li><li>老抽</li><li>淀粉</li><li>盐</li><li>葱</li><li>蒜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>五花肉 500g</li><li>朝天椒 4 条</li><li>小米椒 4 颗</li><li>豆豉 10g，根据个人口味加减 ±5g</li><li>豆瓣酱 10g，根据个人口味加减 ±5g</li><li>老抽 10ml</li><li>淀粉 10g</li><li>盐 1-2g</li><li>葱 0.5-1 根</li><li>蒜 2 瓣</li><li>食用油 15ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>五花肉切片</li><li>把肉放入器皿内，加入淀粉、老抽、盐搅拌腌制半小时</li><li>葱切段</li><li>小米椒、朝天椒斜刀切好</li><li>热锅、倒油</li><li>油热后加入五花肉煸炒。炒至变色后盛出来</li><li>向锅中加蒜，煸出香味，加入豆豉，翻炒均匀</li><li>加入豆瓣酱翻炒均匀</li><li>加入炒好的五花肉继续的翻炒均匀</li><li>加入小米椒、朝天椒、葱段翻炒 40 秒</li><li>出锅。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',11)]))}const m=i(r,[["render",o]]);export{c as __pageData,m as default};
