import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E5%B0%8F%E7%82%92%E9%BB%84%E7%89%9B%E8%82%89.DKIQya6p.jpg",m=JSON.parse('{"title":"小炒黄牛肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/小炒黄牛肉/小炒黄牛肉.md","filePath":"guide/meat_dish/小炒黄牛肉/小炒黄牛肉.md","lastUpdated":1747034379000}'),o={name:"guide/meat_dish/小炒黄牛肉/小炒黄牛肉.md"};function s(d,l,n,_,h,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="小炒黄牛肉的做法" tabindex="-1">小炒黄牛肉的做法 <a class="header-anchor" href="#小炒黄牛肉的做法" aria-label="Permalink to &quot;小炒黄牛肉的做法&quot;">​</a></h1><p><img src="'+r+'" alt="小炒黄牛肉成品"></p><p>小炒黄牛肉是一道简单易做的湘菜。口味十分劲爆爽口。一般初学者只需要 1 小时即可完成</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>牛里脊</li><li>芹菜</li><li>小米椒</li><li>野山椒</li><li>香菜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>牛里脊 400g</li><li>芹菜 200g</li><li>小米椒 30g</li><li>野山椒 30g</li><li>香菜 30g</li><li>食用油 15ml</li><li>酱油 6ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>牛里脊切成不超过 3cm 宽，3mm 厚的薄片，倒入 6ml 酱油，用手抓匀备用</li><li>芹菜切成不超过 5cm 的小段，备用</li><li>小米椒切成丝状，备用</li><li>野山椒切成颗粒，备用</li><li>香菜切成成不超过 3cm 的小段，备用</li><li>热锅，锅内放入 15ml 食用油，大火等待 30 秒让油温升高</li><li>放入小米椒和野山椒爆香</li><li>放入牛里脊和芹菜，然后<strong>大火翻炒 1 分钟</strong></li><li>关火，撒上香菜，盛盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>操作时，全程需要开大火</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const p=i(o,[["render",s]]);export{m as __pageData,p as default};
