import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"小米辣炒肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/小米辣炒肉.md","filePath":"guide/meat_dish/小米辣炒肉.md","lastUpdated":1747107234000}'),r={name:"guide/meat_dish/小米辣炒肉.md"};function o(s,l,d,n,h,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="小米辣炒肉的做法" tabindex="-1">小米辣炒肉的做法 <a class="header-anchor" href="#小米辣炒肉的做法" aria-label="Permalink to &quot;小米辣炒肉的做法&quot;">​</a></h1><p>⚠️注意：不建议清淡饮食的尝试。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>小米辣</li><li>花生油</li><li>五花肉/瘦肉</li><li>盐</li><li>生抽</li><li>蚝油</li><li>大蒜</li><li>姜</li><li>豆瓣酱</li><li>鸡精</li><li>白糖</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>小米椒 20 个，根据个人口味加减</li><li>花生油 20ml</li><li>五花肉/瘦肉 200g</li><li>盐 1-2g</li><li>生抽 10ml</li><li>蚝油 10ml</li><li>姜蒜 50g</li><li>豆瓣酱 10g，根据个人口味加减</li><li>鸡精 10g</li><li>白糖 5g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将小米辣洗净，斜刀切大一点</li><li>肉的话，想切丝切丝，想切片切片，倒入调料（生抽、蚝油、盐）腌制 5 分钟</li><li>热锅倒油，先把肉炒好盛起</li><li>姜蒜爆香，倒入豆瓣酱翻炒，到入切好的小米辣，再倒入瘦肉，翻炒一下，放点生抽、鸡精、盐、糖翻炒</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p><strong>调料可以根据自己的喜好放，也可以加老抽，老抽更香！</strong></p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=i(r,[["render",o]]);export{c as __pageData,p as default};
