import{_ as l,c as t,o as s,ag as e}from"./chunks/framework.DPDPlp3K.js";const i="/assets/%E5%B7%B4%E5%9F%BA%E6%96%AF%E5%9D%A6%E7%89%9B%E8%82%89%E5%92%96%E5%96%B1.DsAOMf43.png",r="/assets/%E7%95%AA%E8%8C%84%E8%93%89.Bti0pLjZ.png",o="/assets/%E7%89%9B%E8%82%89.CAXfbLVE.png",n="/assets/%E6%B2%B9.YWW6G6k-.png",_="/assets/%E5%80%92%E5%85%A5%E7%95%AA%E8%8C%84%E8%93%89.B8ZvRznb.png",p="/assets/%E7%BA%A2.DciF6PzY.png",f=JSON.parse('{"title":"巴基斯坦牛肉咖喱的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/巴基斯坦牛肉咖喱/巴基斯坦牛肉咖喱.md","filePath":"guide/meat_dish/巴基斯坦牛肉咖喱/巴基斯坦牛肉咖喱.md","lastUpdated":1747034379000}'),c={name:"guide/meat_dish/巴基斯坦牛肉咖喱/巴基斯坦牛肉咖喱.md"};function h(d,a,m,g,u,E){return s(),t("div",null,a[0]||(a[0]=[e('<h1 id="巴基斯坦牛肉咖喱的做法" tabindex="-1">巴基斯坦牛肉咖喱的做法 <a class="header-anchor" href="#巴基斯坦牛肉咖喱的做法" aria-label="Permalink to &quot;巴基斯坦牛肉咖喱的做法&quot;">​</a></h1><p><img src="'+i+'" alt="巴基斯坦牛肉咖喱成品"></p><p>Achar gosht（巴基斯坦牛肉咖喱）是一道来自巴基斯坦的特色咖喱菜品。这道菜融合了咖喱的香浓和牛肉的软糯口感，风味独特，偏辣口。它富含优质蛋白质和多种维生素，营养价值丰富。制作过程需要 2.5 小时，步骤并不复杂，是一道适合在周末慢慢烹饪的美味佳肴。</p><p>预估烹饪难度：★★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>普通的炒锅</li><li>电饭煲/电炖锅</li><li>Masala 粉（品牌可选 Shan）</li><li>牛肉</li><li>番茄</li><li>螺丝椒</li><li>原味酸奶</li><li>蒜粉</li><li>姜粉</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>这里以 1kg 牛肉的所需要的配菜量作为标的，可以通过它计算出不同菜量。1kg 牛肉做出的咖喱大概够 5 个人吃。</p><p>1kg 牛肉所需要的配菜量：</p><ul><li>番茄🍅 4 个</li><li>螺丝椒 2 个（大个的）</li><li>原味酸奶 1 盒</li><li>Masala 粉一包 50g</li><li>蒜粉 5g</li><li>姜粉 5g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>全部螺丝椒切成段状，备用</li><li>全部番茄打成番茄蓉，备用 <img src="'+r+'" alt="番茄蓉"></li><li>牛肉切成 2cm 的小块，洗净备用 <img src="'+o+'" alt="牛肉"></li><li>炒锅中倒入一层油（用来防止番茄蓉沸腾蒸发） <img src="'+n+'" alt="油"></li><li>倒入番茄蓉，持续搅拌 2-3 分钟，等待它越变越红 <img src="'+_+'" alt="倒入番茄蓉"><img src="'+p+'" alt="红"></li><li>加入 5g 蒜粉，5g 姜粉和 1 包 50g 的 Masala 粉，搅拌均匀</li><li>加入牛肉和螺丝椒段，搅拌均匀</li><li>加入 1 盒酸奶（为了让整个酱汁变得粘稠），搅拌均匀</li><li>将整锅材料转移到电饭煲/电炖锅，并加入 250 ml 的水，开启炖肉/慢炖档，设定时间 2-3 个小时</li><li>等待完成，开锅检查牛肉软糯，就可以吃了 <img src="'+i+'" alt="巴基斯坦牛肉咖喱成品"></li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>这道菜是从一个巴基斯坦的朋友那里偷师而来，特地整理了笔记，绝对正宗。</p><p>通常配以 烙饼(roti)、馕(naan) 或 米饭 食用。</p><p>如果想做 Chicken karahi 流程几乎是一样的，除了是先用油和各种花椒胡椒煎鸡肉变黄。</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',17)]))}const k=l(c,[["render",h]]);export{f as __pageData,k as default};
