import{_ as i,c as a,o as e,ah as t}from"./chunks/framework.HzbgFGQH.js";const r="/assets/1._vY4yEsk.jpeg",s="/assets/2.BNDFhz2o.jpeg",u=JSON.parse('{"title":"徽派红烧肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/徽派红烧肉/徽派红烧肉.md","filePath":"guide/meat_dish/徽派红烧肉/徽派红烧肉.md","lastUpdated":1747108270000}'),d={name:"guide/meat_dish/徽派红烧肉/徽派红烧肉.md"};function _(n,l,o,h,c,m){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="徽派红烧肉的做法" tabindex="-1">徽派红烧肉的做法 <a class="header-anchor" href="#徽派红烧肉的做法" aria-label="Permalink to “徽派红烧肉的做法”">​</a></h1><p>徽式红烧肉是一道由五花肉等食材制成的菜肴。</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>五花肉</li><li>白砂糖</li><li>食用油</li><li>生抽</li><li>蚝油</li><li>老抽</li><li>姜片</li><li>蒜头</li><li>料酒</li><li>葱</li><li>五香粉</li><li>盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>五花肉 300 g</li><li>白砂糖 100 g</li><li>食用油 200 g</li><li>生抽 10 ml</li><li>蚝油 5 ml</li><li>老抽 5 ml</li><li>姜片 2 片</li><li>蒜头 3 颗</li><li>料酒 100 ml</li><li>葱 1 根</li><li>五香粉 10 g</li><li>盐 10 g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>五花肉切块，每块 2-3 cm 大小</li><li>锅中加入 150 ml 食用油，倒入五花肉，煎炸 2 分钟 后，加入盐，翻炒五花肉，2 分钟 后出锅</li><li>锅中加入 50 ml 食用油，倒入白砂糖，翻炒到咖啡色</li><li>倒入五花肉，翻炒 30 S ，加入姜片、蒜头后翻炒 30 S</li><li>加入料酒，五香粉、葱，加入水没过五花肉，盖上锅盖煮 10 分钟</li><li>加入生抽、老抽、蚝油，中火煮 20 分钟</li><li>开锅，大火烧汁，端盘</li></ul><p><img src="'+r+'" alt="示例菜成品"><img src="'+s+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>葱是整根，不需要切碎</li><li>步骤二加盐的目的是锁住五花肉的油脂</li><li>制作过程，用部分五花肉做了水煮处理，跟未处理的一起做后续制作，发现未水煮的肉质更 Q 弹</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const f=i(d,[["render",_]]);export{u as __pageData,f as default};
