import{_ as l,c as i,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const u=JSON.parse('{"title":"意式烤鸡的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/意式烤鸡.md","filePath":"guide/meat_dish/意式烤鸡.md","lastUpdated":1747108385000}'),t={name:"guide/meat_dish/意式烤鸡.md"};function h(d,a,n,s,o,c){return e(),i("div",null,a[0]||(a[0]=[r('<h1 id="意式烤鸡的做法" tabindex="-1">意式烤鸡的做法 <a class="header-anchor" href="#意式烤鸡的做法" aria-label="Permalink to “意式烤鸡的做法”">​</a></h1><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>鸡腿肉</li><li>盐</li><li>黑胡椒</li><li>橄榄油</li><li>蒜</li><li>柠檬汁</li><li>欧芹</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><ul><li>鸡腿肉用量通常来说为 1-2 个/人</li><li>盐用量为 2 g/人</li><li>黑胡椒用量为 0.5 g/人</li><li>橄榄油用量为 5 ml/人</li><li>蒜用量为 1 瓣/人</li><li>柠檬汁用量为 5ml/人</li><li>欧芹用量为 1 根/人</li></ul><p>使用上述条件，计算出计划使用的原材料比例。</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><h3 id="鸡肉的预处理" tabindex="-1">鸡肉的预处理 <a class="header-anchor" href="#鸡肉的预处理" aria-label="Permalink to “鸡肉的预处理”">​</a></h3><ul><li>鸡腿肉涂上盐、黑胡椒、橄榄油和蒜末</li><li>放入预热至 180 度的烤箱中，烤 30-40 分钟或至熟</li></ul><h3 id="佐料的准备" tabindex="-1">佐料的准备 <a class="header-anchor" href="#佐料的准备" aria-label="Permalink to “佐料的准备”">​</a></h3><ul><li>欧芹切成碎末备用</li><li>柠檬挤出汁备用</li></ul><h3 id="烤制" tabindex="-1">烤制 <a class="header-anchor" href="#烤制" aria-label="Permalink to “烤制”">​</a></h3><ul><li>烤好的鸡肉取出，淋上柠檬汁</li><li>撒上欧芹碎即可</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>烤制时可以将鸡肉淋上蜂蜜或其他烤肉酱提升口感</li><li>烤箱预热至 180 度，烤制时间根据鸡肉的大小和厚度而定，需确保鸡肉熟透</li><li>鸡肉必须全熟才能吃，吃未全熟的鸡肉可能会导致食物中毒和感染细菌，如沙门氏菌和福氏杆菌等。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',17)]))}const m=l(t,[["render",h]]);export{u as __pageData,m as default};
