import{_ as i,c as a,o as e,ah as t}from"./chunks/framework.HzbgFGQH.js";const r="/assets/%E5%A4%A7%E7%9B%98%E9%B8%A1.CplzACzN.jpeg",s="/assets/%E5%A4%A7%E7%9B%98%E9%B8%A1%E7%9A%AE%E5%B8%A6%E9%9D%A2.CvqrWDd-.jpeg",g=JSON.parse('{"title":"新疆大盘鸡的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/新疆大盘鸡/新疆大盘鸡.md","filePath":"guide/meat_dish/新疆大盘鸡/新疆大盘鸡.md","lastUpdated":1747118079000}'),d={name:"guide/meat_dish/新疆大盘鸡/新疆大盘鸡.md"};function n(o,l,_,h,c,p){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="新疆大盘鸡的做法" tabindex="-1">新疆大盘鸡的做法 <a class="header-anchor" href="#新疆大盘鸡的做法" aria-label="Permalink to “新疆大盘鸡的做法”">​</a></h1><p><img src="'+r+'" alt="大盘鸡"></p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>花椒，香叶，香果，干线椒，大蒜，大葱</li><li>油，盐，生抽，蚝油，料酒（可拿啤酒），白糖</li><li>鸡肉（鸡腿肉最好），土豆，菜椒和甜椒（可以不用，加上配色好看）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>2 人食用</p><ul><li>两个火枪腿的鸡肉（这大约是 1kg ）</li><li>土豆 2 个适中大小：750g</li><li>大葱 100g</li><li>菜椒甜椒各一个，各 50g</li><li>白糖 20g</li><li>干线椒 5 个</li><li>大蒜 4 瓣</li><li>油 50g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>肉先剁好，块状，用清水+盐浸泡 5 分钟，去除血水，去腥，然后空干水</li><li>葱蒜辣椒土豆等洗干净，土豆削皮</li><li>葱白切长段，长度 4cm 一段，菜椒和线椒切块状</li><li>土豆切成滚刀土豆，即切一刀动滚动一下，一块土豆大概有 4cm*4cm 大小即可</li><li>炒糖色：先将油加入锅中，然后将白砂糖放入，用锅铲来回搅拌，将糖炒化，然后炒出焦黄色，此时将空干水的鸡肉倒入锅中翻炒，进行上色</li><li>放入花椒，香叶，香果，干线椒等进行翻炒</li><li>放入 5g 盐，生抽 7ml，蚝油 10g ，料酒 100g，倒入 1 升清水，料酒可以用啤酒代替</li><li>调至中火，将水烧开，调制中小火慢炖入味</li><li>当水收至鸡肉即将露出时，将土豆放在锅表面：注意不要翻动土豆，就盖在表面，不然翻到下面容易粘锅，继续盖锅盖炖，炖一会后将大葱，菜椒和甜椒放入，继续炖。</li><li>炖到汁收的差不多时可以进行翻面，将土豆与汤汁相吸，最后关火盛出。</li></ul><p><strong>另：此做法可以吃面，买粗面剂子，将粗面剂子压扁，然后用手拉成宽面，一定要是宽面，下好后可以放入到大盘鸡中，与汤汁搅拌味道极佳！</strong></p><p><img src="'+s+'" alt="大盘鸡皮带面"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const u=i(d,[["render",n]]);export{g as __pageData,u as default};
