import{_ as i,c as e,o as a,ah as r}from"./chunks/framework.HzbgFGQH.js";const o="/assets/%E6%97%A0%E9%AA%A8%E9%B8%A1%E7%88%AA.Dh-sKirS.jpg",p=JSON.parse('{"title":"无骨鸡爪的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/无骨鸡爪/无骨鸡爪.md","filePath":"guide/meat_dish/无骨鸡爪/无骨鸡爪.md","lastUpdated":1747108270000}'),t={name:"guide/meat_dish/无骨鸡爪/无骨鸡爪.md"};function d(s,l,c,n,h,_){return a(),e("div",null,l[0]||(l[0]=[r('<h1 id="无骨鸡爪的做法" tabindex="-1">无骨鸡爪的做法 <a class="header-anchor" href="#无骨鸡爪的做法" aria-label="Permalink to “无骨鸡爪的做法”">​</a></h1><p><img src="'+o+'" alt="无骨鸡爪成品"><strong>图片里的颜色比较浅，家里人爱吃酱油少的</strong></p><p>这是一道做法简单但消耗体力和耐力的无骨鸡爪，酸辣开胃，Q 弹爽口，第一次做的话总耗时 8 个小时 15 分钟。</p><p>预估烹饪难度：★★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>鸡爪</li><li>姜</li><li>料酒</li><li>大葱</li><li>大蒜</li><li>小米辣</li><li>洋葱</li><li>生抽</li><li>蚝油</li><li>黑醋（推荐陈醋）</li><li>白糖</li><li>盐</li><li>花椒油</li><li>香菜</li><li>柠檬</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>一份正好够 2-3 个人吃。</p><ul><li>鸡爪 1kg</li><li>姜 4 片</li><li>料酒 65g</li><li>大葱 3 段（5cm 一段）</li><li>大蒜 10 瓣</li><li>小米辣 4 个少辣，6 个中辣，12 个大辣（推荐大辣）</li><li>洋葱 （半个）</li><li>生抽 75g = 15g * 5</li><li>蚝油 30g = 15g * 2</li><li>黑醋（推荐陈醋） 50g</li><li>白糖 10g</li><li>盐 3g</li><li>花椒油 10ml</li><li>香菜 3 颗</li><li>柠檬 2 颗（以 1 颗为单位来调整酸度）</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><h3 id="鸡爪处理" tabindex="-1">鸡爪处理 <a class="header-anchor" href="#鸡爪处理" aria-label="Permalink to “鸡爪处理”">​</a></h3><ul><li>用剪刀 / 刀 把鸡爪上的指甲的部分全部剪掉 <strong>包括指甲下面的肉和骨头，让它一点指甲都不剩</strong></li><li>用水把他们洗干净，放一边</li><li>把<code>鸡爪</code>放入大锅中，准备去腥味</li><li><code>大葱</code>，<code>料酒</code>，<code>姜</code> 全放进去</li><li>加水没过<code>鸡爪</code></li><li>大火煮开 <strong>中途可以把浮末捞起来</strong></li><li>水开<strong>100度，沸腾</strong>后等 10 分钟</li><li>关火，捞出来，把水沥干，洗干净，放入盆里</li></ul><h3 id="去骨" tabindex="-1">去骨 <a class="header-anchor" href="#去骨" aria-label="Permalink to “去骨”">​</a></h3><p>这一步可以省略，此步骤大约花费 2 小时</p><ul><li>放入冰箱，<strong>冷冻层</strong> 20 分钟</li><li>把全部放入不是冷冻层的冰箱，然后分批<strong>10个一批</strong>拿出来去骨</li><li>从手指（鸡爪的）最前端开始，每只手指都要用刀划开<strong>划到它的手背部分</strong></li><li>再从手背部用刀分划开至整个手臂</li><li>把每只手指关节处都掰一掰<strong>按手指出声音时那种</strong></li><li>按着它的手指最前端，往里推，每只手指都一样，先推到中间手掌手背部分</li><li>每只手指皮脱离后，从手掌开始往手臂部分推直到整个脱下来了</li><li>放入碗中，备用</li></ul><h3 id="调配腌料" tabindex="-1">调配腌料 <a class="header-anchor" href="#调配腌料" aria-label="Permalink to “调配腌料”">​</a></h3><ul><li><code>小米辣</code> 切均匀小颗</li><li><code>大蒜</code>，<code>洋葱</code>，<code>香菜</code>切碎</li><li><code>柠檬</code>对半切开，把柠檬汁挤入鸡爪的容器里</li><li>把<code>全部</code>调料倒入装鸡爪的容器，<code>小米辣</code>，<code>大蒜</code>，<code>洋葱</code>和<code>香菜</code>也放进去</li><li>抓拌均匀</li><li>放入冰箱一个晚上（6 个小时）</li><li>调配好后全部放入准备好的鸡爪</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>操作时，需要注意观察沸腾的水位线，如发现低于 3/4 的食材应加热水至没过食材。</li><li>鸡爪去骨教程 ：<a href="https://www.bilibili.com/video/BV1t44y117D8?share_source=copy_web" target="_blank" rel="noreferrer">bili_89324373958</a></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',20)]))}const m=i(t,[["render",d]]);export{p as __pageData,m as default};
