import{_ as l,c as s,o as t,ag as a}from"./chunks/framework.DPDPlp3K.js";const e="/assets/%E6%9F%B1%E5%80%99%E7%89%9B%E8%85%A9.BpL9d9Zg.jpeg",p="/assets/%E6%9F%B1%E5%80%99%E7%89%9B%E8%85%A9%E9%85%8D%E7%B1%B3%E9%A5%AD.TomrI-3K.jpeg",r="/assets/%E7%89%9B%E8%85%A9%E9%83%A8%E4%BD%8D.BF4Yb5a6.jpeg",o="/assets/%E7%A2%971.DsqYxzgb.jpeg",g="/assets/%E7%A2%972.BtA46gpi.jpeg",c="/assets/%E7%A2%973.SY5XzIdU.jpeg",_="/assets/%E7%A2%974.Bz8n6Xv1.jpeg",m="/assets/%E7%A2%975.D4JX5YAX.jpeg",E="/assets/%E7%A2%976.C51g5yZn.jpeg",n="/assets/%E7%A2%977.Bys5N4ik.jpeg",d="/assets/%E7%89%9B%E8%85%A9%E7%84%AF%E6%B0%B4.t-ROQnD7.jpeg",u="/assets/%E7%89%9B%E8%85%A9%E5%88%87%E5%9D%97.DV1ehXJC.jpeg",h="/assets/%E7%89%9B%E8%85%A9%E6%AD%A4%E6%97%B6%E5%8F%AF%E5%BC%80%E5%A7%8B%E7%82%96%E7%85%AE.CAGpwUuM.jpeg",A="/assets/%E7%89%9B%E8%85%A9%E5%85%A5%E9%94%85.B1lYWY0N.jpeg",B="/assets/%E5%9C%9F%E8%B1%86%E5%88%87%E7%89%87.OVipUHkC.jpeg",b="/assets/%E8%BF%87%E6%BB%A4%E6%B1%A4%E6%B1%81.RnYNXHPr.jpeg",F=JSON.parse('{"title":"柱候牛腩的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/柱候牛腩/柱候牛腩.md","filePath":"guide/meat_dish/柱候牛腩/柱候牛腩.md","lastUpdated":1747107234000}'),j={name:"guide/meat_dish/柱候牛腩/柱候牛腩.md"};function f(q,i,D,P,k,x){return t(),s("div",null,i[0]||(i[0]=[a('<h1 id="柱候牛腩的做法" tabindex="-1">柱候牛腩的做法 <a class="header-anchor" href="#柱候牛腩的做法" aria-label="Permalink to &quot;柱候牛腩的做法&quot;">​</a></h1><p><img src="'+e+'" alt="柱候牛腩成品"><img src="'+p+'" alt="柱候牛腩搭配米饭"></p><p>肉香味美，色泽诱人，滋补强壮，口感甚佳，令人垂涎的广式菜肴。有高压锅只需 1 个小时，否则需要炖煮 3 个小时。</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>炖煮锅，高压锅（可选，但极度推荐！）</li><li>牛腩（首选坑腩，带筋的部位） <img src="'+r+'" alt="牛腩部位"></li><li>柱候酱（核心酱），郫县豆瓣酱，南腐乳，叉烧酱（可选），蚝油， 老抽，生抽</li><li>花雕酒，白酒</li><li>香叶， 花椒，八角，干辣椒，丁香，甘草，干辣椒，小米辣（可选），姜，蒜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>以下为一份两人食用的量：</p><ul><li>牛腩 500-600g</li><li>姜 30g</li><li>蒜半头</li><li>小米辣 1 条（可根据口味调整用量）</li><li>香叶 2 片</li><li>花椒 0.5g</li><li>八角 2 个</li><li>干辣椒 3 个（可根据口味调整用量）</li><li>丁香 3 个</li><li>甘草 2 片</li><li>南腐乳 2 块</li><li>郫县豆瓣酱 15g</li><li>冰糖 10g</li><li>花雕酒 10g</li><li>白酒 20g</li><li>柱候酱 50g</li><li>蚝油 20g</li><li>老抽 5g</li><li>生抽 60g</li><li>叉烧酱 20g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li><p>先把辅料备好：</p><ul><li>碗 1：姜切成 1.5cm*1.5cm 的小块，半头蒜剥好皮，如果有小米辣切成 1cm 小段 <img src="'+o+'" alt="碗 1"></li><li>碗 2：放入 2 片香叶，0.5g 花椒，2 个八角，3 个干辣椒，3 个丁香， 2 片甘草 <img src="'+g+'" alt="碗 2"></li><li>碗 3: 放入 2 块南乳 <img src="'+c+'" alt="碗 3"></li><li>碗 4: 放入 15g 豆瓣酱 <img src="'+_+'" alt="碗 4"></li><li>碗 5: 放入 10g 冰糖 <img src="'+m+'" alt="碗 5"></li><li>碗 6：倒入花雕酒 10g 和白酒 10g <img src="'+E+'" alt="碗 6"></li><li>碗 7：加入 50g 柱候酱，20g 蚝油，20g 叉烧酱，5g 老抽，60g 生抽 <img src="'+n+'" alt="碗 7"></li></ul></li><li><p>牛肉不用切，直接冷水下锅，开大火焯水，水沸腾时将牛肉捞出 <img src="'+d+'" alt="牛腩焯水"></p></li><li><p>冲洗牛肉表面的杂质后，切成 4cm*4cm*4cm 的大块，控干水后放入碗中备用 <img src="'+u+'" alt="牛腩切块"></p></li><li><p>大火，热锅下油，把碗 1（姜、蒜、小米辣）倒入锅中，炒香</p></li><li><p>中小火，倒入碗 2（香料），翻炒均匀，大概 30 秒</p></li><li><p>中小火，放入碗 3（南乳），用锅铲把南乳压碎</p></li><li><p>中小火，放入碗 4（豆瓣酱），翻炒均匀，大概 30 秒</p></li><li><p>中小火，放入碗 5（冰糖），炒至融化</p></li><li><p>中小火，下入牛腩，炒至牛肉上色</p></li><li><p>大火，沿锅边淋入碗 6（酒），快速翻炒，炒至牛肉表面略微焦褐</p></li><li><p>倒入碗 7（酱料），快速翻炒，留意底层汁水，炒至不停冒小气泡，汤汁略微浓稠 <img src="'+h+'" alt="牛腩此时可开始炖煮"></p></li><li><p>将锅内全部食材转移至另一个炖煮锅或高压锅，加水淹过食材 <img src="'+A+'" alt="牛腩入锅"></p></li><li><p>根据使用的锅来选择炖肉的时间：</p><ul><li>a. 普通炖煮锅：小火炖煮 3 个小时</li><li>b. 高压锅：压 40 分钟</li></ul></li><li><p>时间到后开盖调味，如果不够咸加盐或生抽（少量加，不断尝味道，直到合适），不够甜则同理加糖</p></li><li><p>调好味道后便可以把牛腩先捞出</p></li><li><p>如果要吃萝卜土豆，则削皮切成 2cm 厚片倒入锅中煮 10 - 15 分钟（或煮至想要吃的口感），如果是高压锅则在加压煮 5 分钟 <img src="'+B+'" alt="土豆切片"></p></li><li><p>煮好后捞出萝卜土豆和牛腩放一起</p></li><li><p>把汤汁过滤淋入碗中 <img src="'+b+'" alt="过滤汤汁"></p></li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>参考: <a href="https://www.bilibili.com/video/BV12C4y1W7ox" target="_blank" rel="noreferrer">柱候牛腩+茅根马蹄竹蔗水教程</a></li><li>操作高压锅要注意安全哦！</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const T=l(j,[["render",f]]);export{F as __pageData,T as default};
