import{_ as l,c as a,o as e,ah as s}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E6%B9%98%E7%A5%81%E7%B1%B3%E5%A4%AB%E9%B8%AD.DXnQAfDe.jpg",r="/assets/step%E2%91%A0%EF%BC%9A%E5%87%86%E5%A4%87%E7%B1%B3%E7%B2%89.BPP27RCD.jpg",p="/assets/step%E2%91%A1%EF%BC%9A%E7%85%B8%E7%82%92%E9%B8%AD%E5%AD%90.D-uOz24t.jpg",_="/assets/step%E2%91%A2%EF%BC%9A%E7%B1%B3%E7%B2%89%E8%A3%B9%E9%B8%AD.ByULBEDU.jpg",o="/assets/step%E2%91%A3%EF%BC%9A%E9%AB%98%E5%8E%8B%E9%94%85%E8%92%B8%E7%85%AE.D0-sllrW.jpg",A=JSON.parse('{"title":"湘祁米夫鸭的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/湘祁米夫鸭/湘祁米夫鸭.md","filePath":"guide/meat_dish/湘祁米夫鸭/湘祁米夫鸭.md","lastUpdated":1747108270000}'),n={name:"guide/meat_dish/湘祁米夫鸭/湘祁米夫鸭.md"};function E(d,i,c,h,g,m){return e(),a("div",null,i[0]||(i[0]=[s('<h1 id="湘祁米夫鸭的做法" tabindex="-1">湘祁米夫鸭的做法 <a class="header-anchor" href="#湘祁米夫鸭的做法" aria-label="Permalink to “湘祁米夫鸭的做法”">​</a></h1><p><img src="'+t+'" alt="湘祁米夫鸭"></p><p>湖南两祁地区特色菜品，逢年过节家家桌上有。鸭肉被米粉子包裹，入口咸香回味悠长可解乡愁。</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>鸭子（必须新鲜现杀的）</li><li>糯米粉</li><li>粘米粉</li><li>蒸肉粉</li><li>细辣椒粉（吃辣则加）</li><li>白胡椒粉</li><li>五花肉（可加可不加）</li><li>姜蒜</li><li>盐</li><li>食用油</li><li>开水</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>正常 3-4 人吃</p><ul><li>鸭子：1000g</li><li>糯米粉：100g</li><li>粘米粉: 300g</li><li>蒸肉粉: 50g</li><li>细辣椒粉: 50g</li><li>白胡椒粉：5g</li><li>五花肉：50g</li><li>姜蒜：20g</li><li>盐：10g</li><li>食用油：10g</li><li>开水：100g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>将糯米粉、粘米粉、蒸肉粉、细辣椒粉、5 克盐、白胡椒粉倒一起搅匀</li><li>鸭子让热心摊主剁成蒸煮块，姜切片，蒜子剥皮，五花肉切片即可</li><li>热锅凉油煸炒五花肉出油，再加食用油烧热，下入鸭子煸炒</li><li>鸭子煸炒到表皮焦变色，下入姜蒜和盐继续煸炒香味</li><li>关小火倒入米粉翻炒，鸭肉均匀裹满米粉子，加入开水，少量多次的加，边加边翻炒</li><li>翻炒鸭肉和米粉有湿感，铲出入碗中，高压锅放水蒸 20-25 分钟</li><li>出锅前撒点葱花即可享用了</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>这是一道家乡菜。 鸭子品种不限，这里买的是水鸭，价格 24。 蒸 20 分钟，是老鸭的话，蒸一个小时以上。 第一步中粘米粉是主要的粉子，糯米粉是加软糯口感，蒸肉粉是加五香味道，辣椒粉和胡椒粉是加复合香味。</p><p>下面贴出重要步骤以供参考：</p><p><img src="'+r+'" alt="step①：准备米粉.jpg"><img src="'+p+'" alt="step②：煸炒鸭子.jpg"><img src="'+_+'" alt="step③：米粉裹鸭.jpg"><img src="'+o+'" alt="step④：高压锅蒸煮.jpg"></p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',16)]))}const u=l(n,[["render",E]]);export{A as __pageData,u as default};
