import{_ as l,c as i,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const _=JSON.parse('{"title":"烤鸡翅的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/烤鸡翅.md","filePath":"guide/meat_dish/烤鸡翅.md","lastUpdated":1747034379000}'),r={name:"guide/meat_dish/烤鸡翅.md"};function o(d,a,h,s,n,u){return e(),i("div",null,a[0]||(a[0]=[t('<h1 id="烤鸡翅的做法" tabindex="-1">烤鸡翅的做法 <a class="header-anchor" href="#烤鸡翅的做法" aria-label="Permalink to &quot;烤鸡翅的做法&quot;">​</a></h1><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>鸡翅中</li><li>盐</li><li>黑胡椒粉</li><li>酱油</li><li>料酒</li><li>烤箱</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>鸡翅用量为 3-4 个/人</li><li>盐的用量为 2 g/人</li><li>黑胡椒粉的用量为 1 g/人</li><li>酱油的用量为 3 ml/人</li><li>料酒的用量为 3 ml/人</li></ul><p>使用上述条件，计算出计划使用的原材料比例。</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><h3 id="鸡翅的预处理" tabindex="-1">鸡翅的预处理 <a class="header-anchor" href="#鸡翅的预处理" aria-label="Permalink to &quot;鸡翅的预处理&quot;">​</a></h3><ul><li>鸡翅放入碗中</li><li>加盐、黑胡椒粉、酱油、料酒进行腌制 30-40 分钟</li></ul><h3 id="烤制过程" tabindex="-1">烤制过程 <a class="header-anchor" href="#烤制过程" aria-label="Permalink to &quot;烤制过程&quot;">​</a></h3><ul><li>将烤箱预热至 200℃</li><li>将腌制好的鸡翅均匀地放在烤盘上</li><li>将烤盘放入烤箱中层，烤 15-20 分钟</li><li>取出烤盘，将鸡翅翻面，再烤 15-20 分钟，直到熟透</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>可以在腌制鸡翅时加入蜂蜜、酱油、姜蒜粉、五香粉等作为调味料，提升口感和香气</li><li>烤鸡翅时，建议将烤盘底部垫上一层锡纸，便于清洁烤盘</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',15)]))}const m=l(r,[["render",o]]);export{_ as __pageData,m as default};
