import{_ as i,c as a,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const t="/assets/%E7%BA%A2%E7%83%A7%E7%8C%AA%E8%B9%84.Dc0KLvGL.jpg",p=JSON.parse('{"title":"红烧猪蹄的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/红烧猪蹄/红烧猪蹄.md","filePath":"guide/meat_dish/红烧猪蹄/红烧猪蹄.md","lastUpdated":1747108270000}'),s={name:"guide/meat_dish/红烧猪蹄/红烧猪蹄.md"};function d(h,l,n,o,_,m){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="红烧猪蹄的做法" tabindex="-1">红烧猪蹄的做法 <a class="header-anchor" href="#红烧猪蹄的做法" aria-label="Permalink to “红烧猪蹄的做法”">​</a></h1><p><img src="'+t+'" alt="红烧猪蹄"></p><p>红烧猪蹄营养丰富，味道香，汤汁浓郁、下饭强。</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>猪蹄</li><li>香叶</li><li>姜</li><li>葱</li><li>老抽</li><li>桂皮</li><li>冰糖</li><li>料酒</li><li>生抽</li><li>盐</li><li>八角</li><li>食用油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>一份正好够 3-4 人吃。</p><ul><li>猪蹄：2~3 根</li><li>食用油：30ml</li><li>香叶 2 片</li><li>姜 5 片</li><li>葱半根</li><li>桂皮 1 块</li><li>冰糖 7-8 粒</li><li>料酒 30 ml</li><li>生抽 20 ml</li><li>老抽 20 ml</li><li>盐 8 克</li><li>八角 4 个</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><h3 id="开始制作" tabindex="-1">开始制作 <a class="header-anchor" href="#开始制作" aria-label="Permalink to “开始制作”">​</a></h3><ul><li>冷水锅中放入热心摊主剁好的猪蹄，加入 20 ml 料酒与葱姜，煮 15 分钟去掉血腥</li><li>热锅冷油，倒入 30ml 食用油，放入 7-8 粒冰糖，开小火，熬成糖色，期间用锅铲把冰糖压碎，大概熬 2 分钟</li><li>熬成糖色后，放入焯过水的猪蹄，继续小火，翻炒猪蹄，直至所有猪蹄两面微黄</li><li>加入香叶 2 片、桂皮 1 块、八角 4 个、生抽 20 ml、老抽 20 ml、料酒 10 ml、姜 3 片、盐 8 克，转中火、继续翻炒 1 分钟</li><li>加入开水或者冷水，水需要没过猪蹄，盖上锅盖，大火烧开，烧开之后关火</li><li>把锅内的食材全部倒入高压锅中，高压锅中需要 15 分钟（如果同学没有高压锅，可放在锅中大火转小火熬制即可）</li><li>15 分钟之后，把高压锅的食材倒入炒锅中，开大火收汁，此时可用筷子尝下味道、淡的话可以加 2~3g 盐</li><li>大火收汁时长根据锅中的水来算，一般 30 秒即可，多留点也无碍、红烧猪蹄汤也是很下饭的</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const u=i(s,[["render",d]]);export{p as __pageData,u as default};
