import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E7%BE%8A%E6%8E%92%E7%84%96%E9%9D%A2.BZDJzoQm.jpg",c=JSON.parse('{"title":"羊排焖面的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/羊排焖面/羊排焖面.md","filePath":"guide/meat_dish/羊排焖面/羊排焖面.md","lastUpdated":1747034379000}'),o={name:"guide/meat_dish/羊排焖面/羊排焖面.md"};function s(d,l,n,u,h,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="羊排焖面的做法" tabindex="-1">羊排焖面的做法 <a class="header-anchor" href="#羊排焖面的做法" aria-label="Permalink to &quot;羊排焖面的做法&quot;">​</a></h1><p><img src="'+r+'" alt="羊排焖面"> 羊排焖面是一道硬菜，适合聚会时候大展身手。缺点就是有点花时间，优点就是好吃，而且一道菜补齐人体所需的三大营养物质。</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>带皮羊排肉</li><li>青椒</li><li>甜椒</li><li>青椒</li><li>大葱</li><li>花椒</li><li>干辣椒</li><li>生姜</li><li>白砂糖</li><li>老抽</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>1 人份 * 2 顿 = 2 人份</p><ul><li>带皮羊排 500g</li><li>青椒，甜椒 各 2 个</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>羊肉冷水下锅焯水，水开了之后把血沫撇掉，捞出羊肉。</li><li>切好生姜（ 4 片)，放入干辣椒与花椒在碗里备用。</li><li>在炒锅加入油。（多一点也没关系）</li><li>油热之后，放入白砂糖，给羊肉炒出焦糖色。</li><li>羊肉水份炒干之后，放入盐、老抽，以及备好的调味料。</li><li>加入清水没过羊肉，大火煮沸之后，让其继续煮 10 分钟，之后小火炖煮 30 分钟。</li><li>在此期间，可以和面。和面的量以及操作方法在附加内容里讲解 *（注 1）。</li><li>放入青椒，甜椒，大葱，以及面皮进行翻炒。</li><li>翻炒均匀之后，即可出锅。</li></ul><p>*注 1：可以用超市的面条代替，但是尽量选择宽面。</p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>(和面小指南 之 材料）</p><ul><li>中筋面粉 300 g</li><li>盐 3 g</li><li>水 180 ml</li></ul><p>(和面小指南 之 操作）</p><ul><li>先把材料混合均匀，加水边和边搅拌，然后把面团和到光滑</li><li>醒面（ 10 分钟）</li><li>再次和面到光滑</li><li>醒面（ 5 分钟）</li><li>把面团揉成条，切成 6 到 8 个小剂子</li><li>抹上油，等待 5 分钟</li><li>把面团剂子擀成面皮</li><li>放入锅中焖煮 4 分钟（一片一片操作）</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',17)]))}const m=i(o,[["render",s]]);export{c as __pageData,m as default};
