import{_ as i,c as a,o as e,ah as t}from"./chunks/framework.HzbgFGQH.js";const r="/assets/%E8%8A%A5%E6%9C%AB%E7%BD%97%E6%B0%8F%E8%99%BE%E6%88%90%E5%93%81.DSJEcLUk.jpg",g=JSON.parse('{"title":"芥末罗氏虾的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/芥末罗氏虾/芥末罗氏虾.md","filePath":"guide/meat_dish/芥末罗氏虾/芥末罗氏虾.md","lastUpdated":1747118079000}'),s={name:"guide/meat_dish/芥末罗氏虾/芥末罗氏虾.md"};function o(n,l,d,h,_,p){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="芥末罗氏虾的做法" tabindex="-1">芥末罗氏虾的做法 <a class="header-anchor" href="#芥末罗氏虾的做法" aria-label="Permalink to “芥末罗氏虾的做法”">​</a></h1><p><img src="'+r+'" alt="芥末罗氏虾成品"></p><p>本菜品可替换成任意虾种类，包括但不限于基围虾、花虾、黑虎虾等。鲜香入味、芥末风味十足、吃完吮指，且操作十分简单。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>罗氏虾</li><li>蒜</li><li>青芥末</li><li>生抽</li><li>生粉</li><li>胡椒粉</li><li>白糖</li><li>蚝油</li><li>盐</li><li>小米辣（不吃辣可不放或替换成红菜椒）</li><li>黄油</li><li>食用油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份（约 2 人份）：</p><ul><li>罗氏虾 250g</li><li>蒜 1-2 个</li><li>青芥末 20g，用于碗汁</li><li>生粉 10g，用于碗汁</li><li>生抽 30g，用于碗汁</li><li>胡椒粉 5g，用于碗汁</li><li>白糖 3g，用于碗汁</li><li>蚝油 15g，用于碗汁</li><li>盐 3g，用于碗汁</li><li>小米辣 1-2 个</li><li>黄油 20g</li><li>食用油 80ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ol><li><p><strong>处理虾</strong>：</p><ul><li>将虾从背部切开，去除虾线和沙袋，也可从腹部切开，炸出来会胀开，成菜比较漂亮</li><li>用清水洗干净，控干水分后可拍上生粉，也可不拍</li></ul></li><li><p><strong>蒜蓉及碗汁处理</strong>：</p><ul><li>将 2 颗大蒜切成蒜末</li><li>准备碗汁，放入生抽、蚝油、白糖、胡椒粉、盐，依据个人口味挤入芥末，加清水稀释后加入生粉化开</li></ul></li><li><p><strong>炒制过程</strong>：</p><ul><li>锅热倒入食用油，大概能覆盖锅底</li><li>放入控干水分的罗氏虾，慢慢煎制</li><li>虾油煎出来后（表现为锅中出现大量气泡），加入准备好的蒜蓉及小米辣</li><li>闻到蒜蓉的香味后，加入黄油</li><li>黄油融化后翻拌均匀，加入准备好的碗汁</li><li>盖锅盖焖煮 2 分钟汤汁浓稠后出锅</li></ul></li></ol><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li><strong>注意事项</strong>： <ol><li>黄油入锅后不宜炸制太久，融化翻拌均匀即可烹入碗汁</li></ol></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const u=i(s,[["render",o]]);export{g as __pageData,u as default};
