import{_ as i,c as a,o as t,ag as e}from"./chunks/framework.DPDPlp3K.js";const s="/assets/1.CC04b2vt.jpeg",r="/assets/2.dBl0vICc.jpeg",o="/assets/3.D7D75sgd.jpeg",m="/assets/4.DVmgQyV9.jpeg",_="/assets/5.CTOFNu8L.jpeg",c="/assets/6.BKD9tBWR.jpeg",n="/assets/7.bgRupTFi.jpeg",P=JSON.parse('{"title":"荔枝肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/meat_dish/荔枝肉/荔枝肉.md","filePath":"guide/meat_dish/荔枝肉/荔枝肉.md","lastUpdated":1747107234000}'),p={name:"guide/meat_dish/荔枝肉/荔枝肉.md"};function d(g,l,u,h,f,b){return t(),a("div",null,l[0]||(l[0]=[e('<h1 id="荔枝肉的做法" tabindex="-1">荔枝肉的做法 <a class="header-anchor" href="#荔枝肉的做法" aria-label="Permalink to &quot;荔枝肉的做法&quot;">​</a></h1><p>荔枝肉独具闽菜特点，味道酸甜可口。是福州地区比较常见的一道菜肴</p><p>预估烹饪难度：★★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>瘦肉</li><li>凤梨</li><li>鸡蛋</li><li>食用油</li><li>白砂糖</li><li>淀粉</li><li>生抽</li><li>鸡精</li><li>姜末</li><li>芝麻</li><li>番茄酱</li><li>香醋</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>鲜香菇 2 朵</li><li>蟹味菇 30 g</li><li>瘦肉 150 g</li><li>凤梨 100 g</li><li>鸡蛋 1 个</li><li>食用油 500 ml</li><li>白砂糖 5 g</li><li>淀粉 100 g</li><li>生抽 5 ml</li><li>鸡精 5 g</li><li>姜末 5 g</li><li>芝麻 2 g</li><li>番茄酱 20 g</li><li>香醋 2 ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>瘦肉切块（每块 2-3 cm ），放入大碗中，加入鸡蛋 1 个 、生粉 50 g 、生抽 3 ml 、鸡精 2 g</li><li>充分搅拌,直至生粉包裹住瘦肉块（太稀则继续加生粉，太干则加水），然后加入 5 ml 油，在充分搅拌后备用</li><li>在准备一个碗，加入番茄酱、鸡精 3 g 、生抽 2 ml 、姜末、白砂糖、生粉 10 g \\香醋、凉水 200 ml ,充分搅拌后备用</li><li>切一个凤梨, 准备 6 个 （每个 1.5-2 cm）凤梨块</li><li>起锅烧油，倒入 500 ml 油，一直烧油直到听到油炸声</li><li>将瘦肉一个一个放入锅中（切记不可以整碗倒入），保证每个肉不要粘在一起</li><li>全部放入瘦肉后，每 30 S 用勺子来回两面翻转瘦肉块，直至瘦肉块表面金黄</li><li>取出瘦肉，一分钟后倒入油锅中继续炸，直至瘦肉块表面出现焦黄后，取出放入大碗备用</li><li>起锅，倒入汤汁，30 S 后倒入瘦肉块、凤梨块，充分翻炒后 出锅</li><li>摆上芝麻</li></ul><p><img src="'+s+'" alt="示例菜成品"><img src="'+r+'" alt="示例菜成品"><img src="'+o+'" alt="示例菜成品"><img src="'+m+'" alt="示例菜成品"><img src="'+_+'" alt="示例菜成品"><img src="'+c+'" alt="示例菜成品"><img src="'+n+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>更多情况下，福州当地会选用马蹄（解腻）和马铃薯（洗油），因为疫情期间买不到，所以选用了凤梨</li><li>闽菜以甜为主，如果吃不惯的可以放弃白糖</li><li>可以通过勺子敲打瘦肉块，听到声响来判断是否炸透瘦肉</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const x=i(p,[["render",d]]);export{P as __pageData,x as default};
